UPDATE `npc_text_locale` SET `ID`='5279', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Te gusta la alfombra? Yo misma maté y desollé a la bestia. Ojalá todos los centauros cayeran tan fácilmente.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5279') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5281', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es una pena que no podamos desollar a los humanos y a sus aliados. Toda esa piel desperdiciada... Pero bueno... ¿quién querría vestirse con ese cuero tan frágil?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5281') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5285', `Locale`='esES', `Text0_0`='Carne, piel, huesos... un buen cazador no desperdicia nada. Puede recuperarse cada pedacito de cuero, quizás para reforzar la armadura de un aliado. Todos somos fuertes por separado, pero juntos nos hacemos invencibles.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5285') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5287', `Locale`='esES', `Text0_0`='Vivimos y morimos y la tierra permanece, eterna, inalterada por nuestro paso. La tierra no puede pertenecernos, $N. Nosotros pertenecemos a ella. Por ello protegemos la tierra porque sin ella no tenemos ninguna esperanza de sobrevivir.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5287') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5291', `Locale`='esES', `Text0_0`='Dicen que el mismísimo Cairne vive aquí. Cuando haya terminado de fabricar un regalo digno de él, iré a buscarle. Quizás tenga alguna palabra sabia que decirme.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5291') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5294', `Locale`='esES', `Text0_0`='Cuando conseguí arrastrarme fuera de la tumba, creí que mi familia me recibiría con los brazos abiertos. Pero, al contrario, me echaron de la aldea, gritando en un idioma que ya no podía comprender.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5294') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5317', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Escucha detenidamente, $N, porque puedo transmitirte mucho conocimiento.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5317') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5320', `Locale`='esES', `Text0_0`='Randal es un tanto chulo, pero te puedes fiar de él, que es más de lo que puedo decir de otros estudiantes que están conmigo. De todas formas, supongo que no puedo culparles por querer un poco de aventura...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5320') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5330', `Locale`='esES', `Text0_0`='¡Mucho que hacer! No he estado tan ocupado desde que mi primo, accidentalmente, vaporizase todas las telas y el cuero de Gnomeregan. ¡Cielos, sí que todo estuvo húmedo durante un tiempo! Ah, $N, esos eran buenos días...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5330') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5339', `Locale`='esES', `Text0_0`='¿Has venido en busca de instrucción? Quítate el polvo del camino de tu ropa y pongámonos manos a la obra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5339') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5344', `Locale`='esES', `Text0_0`='Me complace tu progreso. Está claro que la Madre Tierra ha inspirado tus manos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5344') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5345', `Locale`='esES', `Text0_0`='De momento, solo hemos conseguido introducirnos en este continente, pero los humanos no pueden luchar con nosotros eternamente. Y después, nos pagarán por habernos esclavizado. Cortaremos sus mentirosas lenguas traidoras.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5345') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5350', `Locale`='esES', `Text0_0`='¿Que si estoy ocupado? ¿Qué se supone que quiere decir eso? ¿Crees que me dedico a estar aquí todo el día jugueteando con mis pulgares? Si has venido a comprar algo, vete a hablar con Handor.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5350') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5354', `Locale`='esES', `Text0_0`='Los giros y recovecos de Orgrimmar están diseñados para frustrar a los invasores. ¿Puedes imaginarte lo rápido que uno de esos raquíticos gnomos se perdería aquí? ¡Ja! Si sirviera como amuleto de suerte, le daría caza y me lo quedaría como mascota.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5354') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5355', `Locale`='esES', `Text0_0`='Antes de mi muerte, fui un bibliotecario; nada que ver con guerras y pestes. Nunca pedí convertirme en esto. Por otra parte, ahora que mi mente me pertenece de nuevo, no pienso rendirme sin pelear.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5355') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5393', `Locale`='esES', `Text0_0`='La hierba es suave y exuberante. El aire es fresco, lleno de luz y música. Todo lo que he visto sugiere que Teldrassil es un sueño hecho realidad. ¿Por qué, entonces, está mi corazón lleno de aprensión?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5393') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5394', `Locale`='esES', `Text0_0`='Has aventajado mucho mis habilidades como para poder enseñarte. Viaja hasta el Pico Nidal en las Tierras del Interior y habla con el enano Drakk Petramano. Su trabajo es magnífico, mucho mejor que el mío.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5394') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='5398', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La aguja es pequeña, pero el hilo es fuerte y las puntadas están bien dadas. Se mantendrá nuestro pacto con Thrall y los otros. La Horda no será expulsada de estas tierras.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5398') AND (`Locale`='esES');
