UPDATE `npc_text_locale` SET `ID`='5855', `Locale`='esES', `Text0_0`='Lo siento, pero no estoy dispuesta a vender caballos a toda persona que no se considera por lo menos exaltado en el Reino de Ventormenta. Estos caballos son los mejores de todo el continente, y ciertamente lo que no queremos es que caigan en manos de nuestros enemigos.$B$BNo quiero decir que tu seas un enemigo, por supuesto... solo tienes que ganarte la confianza de Ventormenta y entonces podremos hacer negocios.', `Text0_1`='Lo siento, pero no estoy dispuesta a vender caballos a toda persona que no se considera por lo menos exaltado en el Reino de Ventormenta. Estos caballos son los mejores de todo el continente, y ciertamente lo que no queremos es que caigan en manos de nuestros enemigos.$B$BNo quiero decir que tu seas un enemigo, por supuesto... solo tienes que ganarte la confianza de Ventormenta y entonces podremos hacer negocios.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='5855') AND (`Locale`='esES');
