UPDATE `npc_text_locale` SET `ID`='6250', `Locale`='esES', `Text0_0`='Feliz Festival de Invierno... mmm... $gpequeño: pequeña;. Los niños buenos tienen un... ehm... descuento especial en cualquier tienda de Pastos de Bosquehumeante. Mmm.', `Text0_1`=NULL, `Text1_0`='Tengo bastante claro lo que el viejo Gran Padre Invierno va a hacer cuando se acabe este turno. Tengo una botella de licor de Festival de Invierno en mi bolsa, y no va a quedar ni una gota.', `Text1_1`=NULL, `Text2_0`='No... No te puedes sentar encima de mí.', `Text2_1`=NULL, `Text3_0`='Sí, claro, adelante, dile al viejo Gran Padre Invierno qué te gustaría recibir este Festival de Invierno.$B$B¡Jo, jo, jo, date prisa!', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6250') AND (`Locale`='esES');
