UPDATE `npc_text_locale` SET `ID`='6334', `Locale`='esES', `Text0_0`='Que Cenariuste guíe, $c.$B$BEstoy aquí, con mis hermanas, en esta tierra corrupta en una misión de gran importancia para el Círculo Cenarion. Pero exijo que solo se dirijan a mí aquellos que tengan el suficiente poder... enviar a un ser de menos poder sería una insensatez.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6334') AND (`Locale`='esES');
