UPDATE `npc_text_locale` SET `ID`='6353', `Locale`='esES', `Text0_0`='No necesito que nadie sepa que todavía tengo un lugar en este mundo, $c. Las tribus verán... todas ellas. Luchan entre ellas y, ¿para qué? Sobras de los buitres... tierras infectadas por la Legión... ¡Bah!$B$BEl centauro debería sentirse orgulloso de su herencia... es tan noble como la de cualquier elfo de la noche u orco... ¡Ya era hora de que empezáramos a actuar como tal!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6353') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6354', `Locale`='esES', `Text0_0`='Por fin soy libre de la corrupción que me tenía atado a este lugar...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6354') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6373', `Locale`='esES', `Text0_0`='Gracias por lo que has hecho, $N.$B$BProtege el Cetro. Que te ayude en tu lucha contra el mal que ha invadido este lugar...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6373') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6380', `Locale`='esES', `Text0_0`='Mucho tiempo atrás, mis hermanas dríades y yo entramos en estas cuevas para liberar el espíritu atormentado de mi tío, Zaetar. Sin embargo, la corrupción que ya se había extendido por estas cuevas, pronto se apoderó de nosotras.$B$BFuimos condenadas a vagar por estos tres pasadizos; las toxinas envenenaban nuestras mentes...$B$BAunque mi espíritu ahora puede descasar, sé que he fallado en mi misión. Y me embarga una gran tristeza...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6380') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6381', `Locale`='esES', `Text0_0`='Ah, Zaetar... <Celebras aparta su mirada.>$B$BMi padre, Rémulos, intentó detenerle...$B$BZaetar era muy obstinado y, cuando se enamoró de una princesa elemental de la tierra, mi padre supo que esa unión estaba condenada, pero Zaetar se negó a escuchar.$B$BLos centauros son el resultado de su error. Nacieron deformes y llenos de odio; mi tío Zaetar murió a manos de sus hijos...$B$BLa malvada princesa, Theradras, sigue guardando sus restos aquí, en las Cuevas de Cristal.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6381') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6383', `Locale`='esES', `Text0_0`='Te doy la bienvenida a la Liga de Expedicionarios. Si necesitas información, y esa información viene en algún libro, quizás pueda ayudarte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6383') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6384', `Locale`='esES', `Text0_0`='Cuando las toxinas que flotaban en Maraudon empezaron a filtrarse en mi mente, comencé a perder el contacto con la realidad. Mis hermanas dríades, preocupadas por si lo utilizaba para hacer el mal, me lo quitaron en secreto y lo partieron en dos antes de esconderlo en las cuevas.$B$BPronto, ellas también cayeron víctimas de la locura y las dos partes del Cetro, desprotegidas, fueron a parar al malvado Lenguavil. El sátiro le confió una de las partes a Noxxion, un elemental malvado, y se quedó con la otra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6384') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6385', `Locale`='esES', `Text0_0`='Sin el conocimiento de los encantamientos necesarios para unir ambas partes, Lord Lenguavil no podía usar el Cetro. Sin embargo, ahora que soy libre de la maldición de la corrupción de Lenguavil, podré ayudarte si quieres traerme las dos partes. La cabeza y la vara del cetro.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6385') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6393', `Locale`='esES', `Text0_0`='Los Hijos de Cenarius, Rémulos y Zaetar.$B$BMi padre, Rémulos, era fuerte y hermoso, y mi tío, Zaetar, muy astuto y de complexión pequeña. Si bien eran respetados por igual, Zaetar siempre sintió que no recibía la misma gloria y atención que su hermano.$B$BDespués de que mis hermanas y yo nacimos, Zaetar, cegado por los celos, partió para intentar superar a su hermano... Fue el creador de su propio fin. Todavía me produce una gran tristeza, incluso con el paso del tiempo...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6393') AND (`Locale`='esES');
