UPDATE `npc_text_locale` SET `ID`='6476', `Locale`='esES', `Text0_0`='¡Saludos, $r! Soy el teniente Haggerdin, oficial a cargo del reclutamiento de la Guardia de Pico Tormenta.$B$BSegún vayas obteniendo prestigio y notoriedad dentro del clan, ven a hablar conmigo y veremos lo que puedo hacer para mejorar tu insignia. Si alguna vez pierdes tu insignia, podré darte una de repuesto.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6476') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6513', `Locale`='esES', `Text0_0`='Nuestros ancestros nos han enseñado que, aunque los individuos deben ser fuertes, la seguridad está en los grupos grandes. ¿Quieres defender el honor de Cima del Trueno organizando una hermandad?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6513') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6514', `Locale`='esES', `Text0_0`='Sí, creo que serías un buen líder. ¿Estás aquí para organizar una hermandad que luche por la Alianza?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6514') AND (`Locale`='esES');
