UPDATE `npc_text_locale` SET `ID`='6535', `Locale`='esES', `Text0_0`='Al maestro Nesingwary le espera una gran cacería. Encuentro mucho más relajante ayudarle a prepararse para un día en la jungla, que para el combate. Aunque echo de menos algunos de esos momentos gloriosos en el campo de batalla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6535') AND (`Locale`='esES');
