UPDATE `npc_text_locale` SET `ID`='6793', `Locale`='esES', `Text0_0`='Tu habilidad supera a la mía, pero he oído que el Viejo Heming en la Bahía del Botín, tiene ejemplares de \"La lubina y tú\". Seguro que te ayuda a mejorar tu habilidad.', `Text0_1`='Tu habilidad supera a la mía, pero he oído que el Viejo Heming en la Bahía del Botín, tiene ejemplares de \"La lubina y tú\". Seguro que te ayuda a mejorar tu habilidad.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6793') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6795', `Locale`='esES', `Text0_0`='¿Ves esa trampa rota de ahí? Los ogros son demasiado estúpidos para arreglarla. ¡Obviamente, necesita que la reajusten un poco! Si la arreglas, seguro que puedes conseguir atraer a Slip\'kik hasta la trampa y, ¡BUM! guardia ogro atrapado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6795') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6796', `Locale`='esES', `Text0_0`='Hay dos minas en Alterac: La Mina Ferrohondo en territorio de la Alianza y la Mina Dentefrío, en territorio de la Horda. Ambas minas son una gran fuente de minerales y, en el pasado, se utilizaban para almacenar suministros de sobra.$B$BAhora, la Horda, la Alianza y los habitantes locales luchan por su control.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6796') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6797', `Locale`='esES', `Text0_0`='Las torres y los búnkeres del Valle de Alterac son puntos de estrategia vitales. Si controlamos una torre, nuestras tropas podrán disparar a gran distancia.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6797') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6798', `Locale`='esES', `Text0_0`='Los cementerios del Valle de Alterac, son donde las almas de los fallecidos encuentran descanso y socorro. Si mueres, tu espíritu irá al cementerio más cercano controlado por los nuestros.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6798') AND (`Locale`='esES');
