UPDATE `npc_text_locale` SET `ID`='6843', `Locale`='esES', `Text0_0`='En el principio de los tiempos, la época que los usurpadores nombraron más tarde como la Primera Era de la Creación, mi maestro y sus hermanos gobernaban este mundo. Fueron ellos, los dioses antiguos, quienes lo crearon todo.$B$BY al final, fueron expulsados por aquellos que se hicieron llamar los Creadores.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6843') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6844', `Locale`='esES', `Text0_0`='Sus formas manifestadas, debilitadas tras millones de batallas, estaban encarceladas bajo el mundo (el mundo que ellos mismos habían creado) cuya superficie penetró la corrupción de los Creadores de titanes y contaminó a los dioses antiguos.$B$BFue ahí y entonces cuando los hermanos se transformaron. Durante cinco milenios se luchó una gran batalla en el corazón del mundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6844') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6846', `Locale`='esES', `Text0_0`='Los capitanes de la Horda y la Alianza han entrado en el campo. Nuestra capitana, Balinda Piedrahogar, dirige a las tropas Pico Tormenta desde la Avanzada Piedrahogar y el capitán enemigo, Galvangar, ladra órdenes desde el interior del Baluarte Sangrehielo.$B$B¡A la batalla!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6846') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6848', `Locale`='esES', `Text0_0`='Los capitanes de la Horda y la Alianza han entrado en el campo. Nuestro capitán, Galvangar, dirige a los Lobo Gélido desde el Baluarte Sangre Fría y su oponente, la capitana Balinda Piedrahogar, se esconde en la Avanzada Piedrahogar.$B$B¡La batalla ha comenzado!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6848') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6853', `Locale`='esES', `Text0_0`='El Valle de Alterac ha sido el hogar del clan Lobo Gélido durante muchos años. No permitiremos que los enanos y su Alianza entren en casa. ¡Y por eso luchamos!$B$BPara recuperar Alterac, hay que derrotar a su general, Vanndar Pico Tormenta, en Dun Baldar.$B$B¡Por la Horda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6853') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6854', `Locale`='esES', `Text0_0`='Los Pico Tormenta descubrieron unos antiguos artilugios en el Valle de Alterac, pero a los orcos Lobo Gélido no les gustan mucho los expedicionarios. Si los enanos tienen que luchar por conseguir la sabiduría de sus ancestros, ¡que así sea!$B$BPara ganar esta batalla, hay que derrotar al orco Drek\'Thar, en el Bastión Lobo Gélido. ¡Que la suerte sea contigo, $c!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6854') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6859', `Locale`='esES', `Text0_0`='La capitana Balinda Piedrahogar lidera a las tropas de la Alianza de la primera línea. Está en la Avanzada Piedrahogar, en la sección norte de la Tierra de Disputa.$B$BActualmente Balinda está en la Avanzada Piedrahogar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6859') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6861', `Locale`='esES', `Text0_0`='Drek\'Thar es el líder militar y espiritual del clan Lobo Gélido en Alterac, lidera a los suyos desde el Bastión Lobo Gélido.$B$BSi le derrotamos, las tropas de la Horda en Alterac se desmoronarán.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6861') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6863', `Locale`='esES', `Text0_0`='El capitán Galvangar es la mano derecha de Drek\'Thar. Lidera a las tropas de la Horda desde el Baluarte Sangrehielo, en la sección sur de la Tierra de Disputa.$B$BEl capitán Galvangar está en el Baluarte Sangrehielo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6863') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6867', `Locale`='esES', `Text0_0`='Ocurrió durante el Cataclismo Elemental. El Señor del Fuego, Ragnaros, pretendía consumir a mi Señor, Thunderaan, Príncipe del Viento.$B$B<Demitrian inclina la cabeza con un gesto de cólera.>$B$BPronuncia su nombre y decoraré este campo arrasado con tus entrañas. Permanece en silencio y escúchame.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6867') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6868', `Locale`='esES', `Text0_0`='Fueron los tenientes de Ragnaros, Geddon y Garr, quienes perpetraron la traición contra mi Señor. Cogieron por sorpresa a Thunderaan.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6868') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6869', `Locale`='esES', `Text0_0`='Thunderaan no tenía ni idea de los avances del Señor del Fuego. Antes de que mi Señor pudiera reaccionar, Sulfuras, el legendario martillo de Ragnaros, descargó un golpe mortal. Thunderaan cayó, derrotado.$B$BEl Señor del Fuego se dio un festín con la esencia de mi Señor, pero no consiguió consumirlo completamente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6869') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6870', `Locale`='esES', `Text0_0`='Lo poco que quedaba de la esencia de Thunderaan, se guardó en un talismán de vínculo elemental. A continuación, Ragnaros partió el talismán en dos partes perfectamente iguales. Entregó las piezas a sus tenientes...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6870') AND (`Locale`='esES');
