UPDATE `npc_text_locale` SET `ID`='6882', `Locale`='esES', `Text0_0`='Mmm... ¿Tú matar al rey, sí? Eso significa que tú ocupar su lugar. ¡Eso te convierte en $gel nuevo rey:la nueva reina;! ¡Viva $gel rey:la reina;!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6882') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6883', `Locale`='esES', `Text0_0`='Bueno, por lo que oí mientras estaba esperando a que esos palurdos me comiesen... quizás no sea necesario matar a todos los ogros que encuentres.$B$BEl otro día, los ogros hablaban todo el rato de cómo llegó el rey Gordok al poder... Mató al anterior rey y ocupó su lugar. Los esbirros del rey, a los que Gordok perdonó la vida, juraron lealtad inmediatamente al nuevo rey.$B$BQuién sabe... A lo mejor el ogro que salvaste podría ser... ¡En sentido figurado, por supuesto!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6883') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6895', `Locale`='esES', `Text0_0`='Vale, $gjefe:jefa;, cuando quieras el tributo de los esbirros, ¡decírmelo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6895') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6896', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Vete de aquí, $gforastero:forastera;, o te consumirá como ha hecho con todo lo que toca.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6896') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6897', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Conoces a los Altonato? ¿Los Shen\'dralar?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6897') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6898', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los Shen\'dralar eran los arcanistas más venerados de la reina Azshara. Fue entre estos muros, donde se cumplieron las demandas de la reina, a menudo en secreto.$B$BDespués de la destrucción del pozo y el subsiguiente letargo que se apoderó de los Altonato y de las sectas menores de elfos de la noche, el príncipe de Eldre\'Thalas, Tortheldrin, comenzó la construcción de las torres que ves en las salas del ala oeste.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6898') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6899', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='El Príncipe planeó encarcelar a un demonio de gran poder en un campo de fuerza. Las torres canalizarían las energías latentes hacia el campo, manteniéndolo operativo.$B$BMientras estuviera atrapado, Immol\'thar serviría como energía para el pueblo de Eldre\'Thalas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6899') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6900', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Las energías absorbidas sustentarían la magia de los Altonato, permitiendo a aquellos que estaban en Eldre\'Thalas alimentar su infinita y creciente magia.$B$BMiles de años pasaron, y el poder requerido para mantener encarcelado a Immol\'thar a la vez que se mantenía la vida de Eldre\'Thalas, comenzó a cambiar. El coste de mantener a Immol\'thar, comenzó a eclipsar la cantidad de energía que podía ser absorbida y distribuida. La antigua red perfecta se convirtió en una gran pérdida.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6900') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6901', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los fantasmas Altonato que ves vagando por el patio y las salas de Eldre\'Thalas fueron asesinados, no por ogros Gordok invasores ni sátiros, ¡sino por el mismísimo Príncipe!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6901') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6902', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Fueron destruidos para preservar la vida inmortal del Príncipe y aumentar sus ilimitados poderes. El poder antiguamente dividido entre miles, ahora solo pertenece al príncipe y los zelotes de Shen\'dralar. Los que sobrevivieron, emanan poder directamente en las torres para mantener encarcelado a Immol\'thar.$B$BSi el demonio rompiese sus cadenas, seguramente destruiría lo poco que queda de este antiguo bastión de los poderes arcanos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6902') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6909', `Locale`='esES', `Text0_0`='Durante 10000 años he gobernado este reino. Durante ese tiempo, quienes han pasado por los terrenos sagrados de la Casa de Shen\'dralar, me han contado muchas historias fascinantes.$B$BYo estoy atado a este lugar y, a menudo, intercambio historias con los visitantes. ¿Tienes una historia para mí, o quizás quieras escuchar una?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6909') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6911', `Locale`='esES', `Text0_0`='No debemos enfurecer al Maestro. Su ira es cruel e irracional.', `Text0_1`='No debemos enfurecer al Maestro. Su ira es cruel e irracional.', `Text1_0`='Habla bajo, $gforastero:forastera;. No queremos perturbar la meditación del Maestro.', `Text1_1`='Habla bajo, $gforastero:forastera;. No queremos perturbar la meditación del Maestro.', `Text2_0`='Muchos han venido a este santuario, muchos se han ido.', `Text2_1`='Muchos han venido a este santuario, muchos se han ido.', `Text3_0`='El Tradicionalista es un Ancestro. ¡Ha servido a nuestra gloriosa reina!', `Text3_1`='El Tradicionalista es un Ancestro. ¡Ha servido a nuestra gloriosa reina!', `Text4_0`='Últimamente están desapareciendo libros. El Tradicionalista y sus asistentes están desesperados. Sospechan que alguien de Eldre\'Thalas tiene la culpa.', `Text4_1`='Últimamente están desapareciendo libros. El Tradicionalista y sus asistentes están desesperados. Sospechan que alguien de Eldre\'Thalas tiene la culpa.', `Text5_0`='¿Ha contado el Príncipe la historia de la Crematoria? Es nuestra historia favorita. Su favorita es la continuación de la Crematoria. Nadie ha conseguido que le contara esa historia.', `Text5_1`='¿Ha contado el Príncipe la historia de la Crematoria? Es nuestra historia favorita. Su favorita es la continuación de la Crematoria. Nadie ha conseguido que le contara esa historia.', `Text6_0`='Sospecho que los diablillos están robando conocimiento.', `Text6_1`='Sospecho que los diablillos están robando conocimiento.', `Text7_0`='El Maestro tiene un gran interés por la Guarida de Nefarian. Ojalá alguien pudiera vencer al dragón y a sus esbirros y volver a contar la historia al Príncipe. Apuesto a que eso conseguiría hacer que el príncipe hablase. ¡Podríamos encontrar la Crematoria!', `Text7_1`='El Maestro tiene un gran interés por la Guarida de Nefarian. Ojalá alguien pudiera vencer al dragón y a sus esbirros y volver a contar la historia al Príncipe. Apuesto a que eso conseguiría hacer que el príncipe hablase. ¡Podríamos encontrar la Crematoria!' WHERE (`ID`='6911') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6916', `Locale`='esES', `Text0_0`='Todos los esbirros del jefe: el vapuleador Kreeg, los tres guardias, el capitán Kromcrush y el observador, dar tributo $gal nuevo jefe:a la nueva jefa;. Si te cargas a un esbirro, ese esbirro no podrá darte un tributo. Por cada uno que mantengas con vida, más tributo recibirás. ¡Mantenlos a todos con vida y tú recibir el GRAN tributo!$B$B¡Sí!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='6916') AND (`Locale`='esES');
