UPDATE `npc_text_locale` SET `ID`='6922', `Locale`='esES', `Text0_0`='¡Viva $gel nuevo rey:la nueva reina;!', `Text0_1`=NULL, `Text1_0`='Cuando te miré mal antes... antes de que te convirtieras en $gel nuevo rey:la nueva reina;... Mmm... Solo estaba bromeando.$B$BSí.', `Text1_1`=NULL, `Text2_0`='¡Los otros ogros fueron tontos por atacarte, $gjefe:jefa;! Por eso yo seguir con vida, ¡soy listo y todo eso!', `Text2_1`=NULL, `Text3_0`='Nosotros mucho más limpios que los ogros de Tanaris. Por todo el desierto hasta donde poder ver, caca de ogro. ¡Aquí no, no $gseñor: señora;!', `Text3_1`=NULL, `Text4_0`='Mientras te quedes aquí con nosotros, tú ser $grey:reina;. Tú marcharte y algún otro ogro ocupará tu lugar. ¡Así funcionamos los Gordok!', `Text4_1`=NULL, `Text5_0`='¡Yo muy listo! ¡L-S-T-O!', `Text5_1`=NULL, `Text6_0`='¿El viejo rey está muerto? ¡Imposible!', `Text6_1`=NULL, `Text7_0`='No te causaré problemas, $gjefe:jefa;.', `Text7_1`=NULL WHERE (`ID`='6922') AND (`Locale`='esES');
