UPDATE `npc_text_locale` SET `ID`='6992', `Locale`='esES', `Text0_0`='¿Saliste de una mazmorra? ¿Me conseguiste algo?', `Text0_1`=NULL, `Text1_0`='¡Cuando crezca, quiero ser el rey de Ventormenta!', `Text1_1`=NULL, `Text2_0`='He estado en Forjaz antes. Huele muy gracioso allí.', `Text2_1`=NULL, `Text3_0`='Los champiñones siempre crecen en lugares húmedos y es por eso que parecen paraguas.', `Text3_1`=NULL, `Text4_0`='Si los overoles se sostienen con los botones de arriba, ¿porqué las correas van hasta atrás?', `Text4_1`=NULL, `Text5_0`='¿Qué pasa si tus mocos se congelan en tu nariz?', `Text5_1`=NULL, `Text6_0`='¿Alguna vez les da sed los peces?', `Text6_1`=NULL, `Text7_0`='Si intentas fallar y lo logras, ¿cómo se llama eso?', `Text7_1`=NULL WHERE (`ID`='6992') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='6993', `Locale`='esES', `Text0_0`='Tu armadura parece escalofriante. ¿Tú también eres escalofriante?', `Text0_1`=NULL, `Text1_0`='Si eres $gun verdadero héroe:una verdadera heroína; de la Horda, ¡entonces quiero verte luchar con un dragón! ¡Sí!', `Text1_1`=NULL, `Text2_0`='Cuando un rayo golpea el océano, ¿porqué no mueren todos los peces?', `Text2_1`=NULL, `Text3_0`='¿Volar sin alas se llamaría caminar?', `Text3_1`=NULL, `Text4_0`='Tengo una piedra en mi nariz.', `Text4_1`=NULL, `Text5_0`='Si una tortuga no tiene caparazón, ¿está sin hogar o desnuda?', `Text5_1`=NULL, `Text6_0`='¿Qué quiere decir \"zug zug\"?', `Text6_1`=NULL, `Text7_0`='Si tu guante es muy grande, ¿todavía encaja como guante?', `Text7_1`=NULL WHERE (`ID`='6993') AND (`Locale`='esES');
