UPDATE `npc_text_locale` SET `ID`='7156', `Locale`='esES', `Text0_0`='He... fallado, $N. El Señor de Roca Negra... El Señor de Roca Negra es demasiado poderoso. He consumido mi energía con... con facilidad. Me he convertido en... Yo... mátame. Debes... matarme.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7156') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7175', `Locale`='esES', `Text0_0`='¿No ves que estoy pescando aquí?', `Text0_1`=NULL, `Text1_0`='¡Has espantado a los peces! ¿Qué quieres?', `Text1_1`=NULL, `Text2_0`='¿Qué puedo hacer por ti, $c?', `Text2_1`=NULL, `Text3_0`='¡No me muevo de aquí hasta que pesque un mero de 130 kilos! Pero creo que tú quieres largarte a otra parte, ¿tengo razón?', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7175') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7176', `Locale`='esES', `Text0_0`='Mira al dragón caído, $r. ¡El poder de la Horda es incuestionable!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7176') AND (`Locale`='esES');
