UPDATE `npc_text_locale` SET `ID`='7241', `Locale`='esES', `Text0_0`='Pescar es guay, colega. ¿Sabes? Debo mi felicidad a mi mentor, el mejor pescador de to\'l mundo, Nat Pagle. ¿Has oído hablar de él, colega?$B$BSi tás buscando la felicidad, Nat Pagle puede enseñarte el camino.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7241') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7242', `Locale`='esES', `Text0_0`='Zul\'jin volverá algún día, $r. Acuérdate de lo que te digo. Hasta entonces, todos mantenemos nuestra posición y hacemos todo lo que podemos para que la población trol del bosque permanezca intacta.$B$BDicho eso, como maestro cazador del Poblado Sañadiente, es mi responsabilidad asegurar que el mundo que nos rodea, es un lugar seguro para habitar.$B$BHay que controlar a las bestias de esta zona.$B$B¿Estás buscando trabajo?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7242') AND (`Locale`='esES');
