UPDATE `npc_text_locale` SET `ID`='7245', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No te acerques demasiado, $N. Puedo aplastarte y abrirte el cerebro a golpes con mi martillo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7245') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7247', `Locale`='esES', `Text0_0`='Atrás o te cortaré en dos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7247') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7249', `Locale`='esES', `Text0_0`='Acabo de completar un nuevo implosionador dimensional que me hará más rico de lo que cualquier goblin puede soñar. Todo lo que necesito es un ingeniero gnomo capacitado para construir el desgarrador dimensional que lo conecta y probarlo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7249') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7251', `Locale`='esES', `Text0_0`='Gracias a la tecnología que consiguieron recuperar los astutos gnomos que se adentraron en Gnomeregan, he conseguido construir mi mayor invento: ¡El transportador de materia! Ahora, todo lo que necesito es un hábil ingeniero gnomo para construir un potente localizador que se adapte al transportador.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7251') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7256', `Locale`='esES', `Text0_0`='Voy a realizar un último servicio. Rezo para que sea suficiente...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7256') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7257', `Locale`='esES', `Text0_0`='¡Coge la llave y abre estas cadenas! La tiene Hitah\'ya el Guardián... En la parte de atrás de la cueva...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7257') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7274', `Locale`='esES', `Text0_0`='Ofertas de trabajo/Perdido y encontrado/Se busca:', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7274') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7314', `Locale`='esES', `Text0_0`='¡$GBienvenido:Bienvenida;! Te doy la bienvenida al Zoo de Singularidades Luna Negra de Yebb Tesorillos: ¡Un tesoro de leyendas vivientes y rarezas míticas! ¡Que disfrutes de la visita, $gamigo:amiga;!$B$B¡Pero cuidado con lo que tocas! En las exhibiciones hay bestias y artefactos tanto letales como divinos. Podrían quemar la mano caprichosa de un observador demasiado curioso...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7314') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7315', `Locale`='esES', `Text0_0`='¿Dónde te puedo mandar hoy, $c?', `Text0_1`=NULL, `Text1_0`='El Cruce parece ser el centro de actividad de Los Baldíos, yo prefiero la tranquilidad del Campamento Taurajo. ¿Dónde te voy a mandar?', `Text1_1`=NULL, `Text2_0`='Muchos son los caminos de la Madre Tierra. Decidas donde decidas ir, que ella te proteja.', `Text2_1`=NULL, `Text3_0`='Trata bien al jinete del viento, ya que te lleva a tu destino. Que tus ancestros te protejan, $N.', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7315') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7334', `Locale`='esES', `Text0_0`='¡Hola, $gamigo:amiga;, te doy la bienvenida al mayor espectáculo de todo Azeroth!$B$BPor favor, pasa y disfruta de todo lo que podemos ofrecerte. ¡Sorpréndete con las maravillas que la Feria de la Luna Negra ha descubierto en este vasto y misterioso mundo! No hemos reparado en gastos para poder ofrecer una diversión tan increíble que deleitará a niños de todas las edades.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7334') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7336', `Locale`='esES', `Text0_0`='¡No todo se trata de dinero! Recibimos donaciones muy generosas, algunas de ellas extremadamente sustanciales. Y, además, nuestra compañía está motivada por la necesidad de animar este mundo devastado por la guerra. No te preocupes por nosotros. Deja que nosotros nos preocupemos por cómo divertirte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7336') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7337', `Locale`='esES', `Text0_0`='¡Canje de vales! ¡Cambiamos vuestros vales por premios! ¡Venid!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7337') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7338', `Locale`='esES', `Text0_0`='Vete de aquí, $gpalurdo:palurda;.', `Text0_1`=NULL, `Text1_0`='Esto no es una atracción, $gpaleto:paleta;. Lárgate.', `Text1_1`=NULL, `Text2_0`='Si estás intentando ganarme con tu simpatía, no gastes saliva. O mejor, trágatela toda a ver si te ahogas...', `Text2_1`=NULL, `Text3_0`='Por si no ha quedado claro, no estoy aquí de relaciones públicas.', `Text3_1`=NULL, `Text4_0`='Si te metes con Silas, te metes conmigo. Que tengas un buen día.', `Text4_1`=NULL, `Text5_0`='Necesito tanto una segunda cabeza, como perder la primera...', `Text5_1`=NULL, `Text6_0`='Viví en La Masacre durante un tiempo. Fue divertido. Mucho.', `Text6_1`=NULL, `Text7_0`='Al contrario de muchos ogros, yo sí sé para lo que sirve una letrina exterior, para darte una paliza. Y ahora, déjalo.', `Text7_1`=NULL WHERE (`ID`='7338') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7339', `Locale`='esES', `Text0_0`='Sabía desde hace mucho tiempo que vendrías a buscarme, $N.$B$BTodos los seres sensibles del mundo dependen en gran medida de las decisiones que toman. Las decisiones conllevan más decisiones y, estas, nos convierten en lo que somos. Ahí es yace tu fortuna, quién eres y las decisiones que tomas.$B$B¿$gListo:lista; para descubrir dónde yace tu fortuna?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7339') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7340', `Locale`='esES', `Text0_0`='Pues muy bien...$B$BTu señor te ha encargado la tarea de proteger sus campos de maíz de cazadores furtivos y ladrones. Una noche de patrulla, te encuentras con un hombre demacrado con una túnica raída que está robando en el campo. Rápidamente, le confrontas y él te suplica que le perdones la vida. Dice que está robando maíz para alimentar a su familia porque el señor de la tierra, tu señor, exige demasiados impuestos. De hecho, tu señor es famoso por su dureza a la hora de imponer tasas en sus tierras.$B$BToma tu decisión.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7340') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7341', `Locale`='esES', `Text0_0`='Te espera tu próxima decisión.$B$BEres el verdugo de tu señor. Te han enviado a un hombre para que le ejecutes por traicionar a tu señor y a tu pueblo. Conoces a este individuo. Era un buen amigo tuyo que, misteriosamente, desapareció un día. También sabes que los crímenes por los que ha sido juzgado, han causado la muerte de muchos civiles inocentes. Cuando te ve, te suplica que le perdones la vida y dice que una gran autoridad le tendió una trampa para incriminarle.$B$BToma tu decisión.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7341') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7354', `Locale`='esES', `Text0_0`='¿No ves que tengo trabajo que hacer?', `Text0_1`='¿No ves que tengo trabajo que hacer?', `Text1_0`='¡La feria va a llegar muy pronto!', `Text1_1`='¡La feria va a llegar muy pronto!', `Text2_0`='¡Tienes razón! La Feria de la Luna Negra va a estar justo aquí. En cuanto descargue esos enormes vagones, monte todas las tiendas, el zoo y prepare las bebidas. ¡Asegúrate de volver!', `Text2_1`='¡Tienes razón! La Feria de la Luna Negra va a estar justo aquí. En cuanto descargue esos enormes vagones, monte todas las tiendas, el zoo y prepare las bebidas. ¡Asegúrate de volver!', `Text3_0`='La Feria de la Luna Negra abrirá pronto. Ven un poco más tarde a ver si ya estamos listos.', `Text3_1`='La Feria de la Luna Negra abrirá pronto. Ven un poco más tarde a ver si ya estamos listos.', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7354') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7355', `Locale`='esES', `Text0_0`='¡Te doy la bienvenida a la feria, joven! Tenemos maravillas de todos los confines del mundo para ti. Oh, y por favor, avísame si consigues reunir una baraja de cartas menores de la Luna Negra, al maestro... quiero decir, a mí, me interesaría mucho...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7355') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7356', `Locale`='esES', `Text0_0`='Hemos esparcido cartas de la Luna Negra por todo el mundo, para ver si algún individuo capaz, podría completar uno de los ocho palos. Si consigues reunir las ocho cartas de uno de los palos, te recompensaremos con una de las cartas superiores de la Luna Negra, que funciona como una potente alhaja, además de ser un vínculo a la Feria de la Luna Negra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7356') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7357', `Locale`='esES', `Text0_0`='Si consigues completar una baraja de Bestias, te recompensaremos con un naipe superior de la Luna Negra del dragón azul. Es muy útil para restaurar energías mágicas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7357') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7358', `Locale`='esES', `Text0_0`='Si consigues completar una baraja de Portales, te recompensaremos con un naipe superior de la Luna Negra de El Vacío Abisal. Este naipe puede volver a meter tu espíritu en tu cuerpo cuando hayas muerto y restaurar tu salud.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7358') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7359', `Locale`='esES', `Text0_0`='Si consigues completar una baraja de Elementales, te recompensaremos con un naipe superior de la Luna Negra de La Vorágine. Este naipe puede causar un gran daño a un enemigo con quien estés luchando.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7359') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7360', `Locale`='esES', `Text0_0`='Si consigues completar una baraja de Señores de la Guerra, te recompensaremos con un naipe superior de la Luna Negra del Heroísmo. Este naipe puede curarte cuando estés en combate de melé.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7360') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7362', `Locale`='esES', `Text0_0`='Te aguarda tu siguiente elección.$B$BTu hermano desea unirse a las filas de la prestigiosa orden militar de la que tú ya formas parte. Aunque no dudas de su compromiso y lealtad, cuestionas su talento y su habilidad para realizar las arduas tareas de la orden. Aunque no sabes con seguridad si podrá estar a la altura del desafío de la orden – poniéndote a ti y a otros en peligro – tienes por seguro de que solo tu palabra bastaría para permitirle que ingrese o se quede fuera.$B$BHaz tu elección.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7362') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7363', `Locale`='esES', `Text0_0`='Te espera tu próxima decisión.$B$BHas vencido a una bestia terrible que estaba arrasando el país y le has cortado la oreja como trofeo. Más tarde, te enteras de que tu señor ha ofrecido una recompensa por la muerte de la bestia y que un noble caballero que se ha quedado en la indigencia, y de quien sabes que está sufriendo para mantener a su familia, ha dicho haber matado a la bestia. En realidad no necesitas el dinero, pero sabes que el caballero está mintiendo.$B$BToma tu decisión.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7363') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7364', `Locale`='esES', `Text0_0`='Tu fortuna ha sido decidida, $c. Durante las próximas horas, encontrarás que tu espíritu adopta la naturaleza de tu verdadero ser. Ten cuidado, ya que tu fortuna, y tu destino, nunca son tan transparentes como puedes creer.$B$BSi quieres una pequeña visión para ayudarte a considerar tu futuro, dímelo. Te daré encantado tu fortuna por escrito, siempre que tengas espacio en tu inventario. Todo ha sido el resultado de tus respuestas a mis preguntas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7364') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7365', `Locale`='esES', `Text0_0`='Aquí tienes tu fortuna, $N. Espero que te ayude.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7365') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7366', `Locale`='esES', `Text0_0`='¡Llega a la ciudad la Feria de la Luna Negra! ¡Que nadie se lo pierda!', `Text0_1`=NULL, `Text1_0`='¡Aseguraos de ver todas las maravillas increíbles de la Feria de la Luna Negra cuando llegue a la ciudad!', `Text1_1`=NULL, `Text2_0`='¡Llega la Feria de la Luna Negra! ¡Diversión para niños de TODAS las edades! ¡Vamos, venid todos!', `Text2_1`=NULL, `Text3_0`='Venid todos, ¡el espectáculo más asombroso de todo Azeroth, la Feria de la Luna Negra! ¡Hurra!', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7366') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7374', `Locale`='esES', `Text0_0`='¡Está aquí! ¡La Feria de la Luna Negra está abierta! ¡No os perdáis el espectáculo más excitante de Azeroth!', `Text0_1`=NULL, `Text1_0`='¡Llega a la ciudad la Feria de la Luna Negra! ¡El espectáculo más apasionante que jamás hayáis visto!', `Text1_1`=NULL, `Text2_0`='La Feria de la Luna Negra solo estará abierta durante un breve período de tiempo antes de que se bajen las cortinas. ¡Aseguraos de venir hoy!', `Text2_1`=NULL, `Text3_0`='¡Niños y niñas de todas las edades, ha llegado la Feria de la Luna Negra! ¡Hurra!', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7374') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7377', `Locale`='esES', `Text0_0`='Ahora mismo, la Feria de la Luna Negra está en las doradas praderas de Mulgore, al suroeste de Cima del Trueno. ¡Que nadie se lo pierda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7377') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7382', `Locale`='esES', `Text0_0`='Seguro que hay muchos bichejos por aquí.', `Text0_1`='Seguro que hay muchos bichejos por aquí.', `Text1_0`='¿Te lo estás pasando bien?', `Text1_1`='¿Te lo estás pasando bien?', `Text2_0`='¡No te olvides de comprar comida y regalos!', `Text2_1`='¡No te olvides de comprar comida y regalos!', `Text3_0`='La Feria de la Luna Negra es el espectáculo más importante de todo Azeroth.', `Text3_1`='La Feria de la Luna Negra es el espectáculo más importante de todo Azeroth.', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7382') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7383', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Te doy la bienvenida a la Feria de la Luna Negra, $c!$B$BMe llamo Kerri Hicks y estrecharía tu mano, pero me da miedo romperla, ¡porque soy la mujer más fuerte del mundo!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7383') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7387', `Locale`='esES', `Text0_0`='¡Venid! ¡Alitas súper picantes aquí mismo!', `Text0_1`=NULL, `Text1_0`='Perritos de la Luna Negra, ¡recién sacados de la barbacoa! Venid a probarlos mientras están calientes.', `Text1_1`=NULL, `Text2_0`='Tú tienes dinero y nosotros, comida. ¡¿Qué estás esperando?!', `Text2_1`=NULL, `Text3_0`='¡Edición limitada! Rana crujiente solo aquí, en la Feria de la Luna Negra.', `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7387') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7389', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Todo lo que tengo a la venta lo he adquirido yo misma. Tienes mi garantía personal de autenticidad.', `Text1_0`=NULL, `Text1_1`='¿Cartas de la Feria de la Luna Negra? Busca al Profesor. Ven a verme si eres tratante de extrañas antigüedades y artefactos.', `Text2_0`=NULL, `Text2_1`='Echa un vistazo a mis mercancías mientras todavía puedas. Estaremos aquí toda la semana y, después, nos iremos a algún sitio remoto.', `Text3_0`=NULL, `Text3_1`='¿Tienes alguna idea de lo que tengo que sufrir para traer estos incomparables objetos y ponerlos a la venta? ¡Ven a verlo con tus propios ojos!', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7389') AND (`Locale`='esES');
