UPDATE `npc_text_locale` SET `ID`='7535', `Locale`='esES', `Text0_0`='Saludos, $c. Es la época del Festival de la Cosecha. Época de grandes regalos y de conmemoración. Participamos en banquetes para celebrar todo aquello por lo que tenemos que dar gracias, y recordamos a nuestros ancestros y héroes pasados, dando gracias por el legado que nos han dejado.$B$BParticipa en la celebración. También voy a ofrecer unos fuegos artificiales para celebrar este jubiloso momento del año.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7535') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7554', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡No podemos permitir que la Alianza expanda su posición de poder en Arathi! Incluso su minúsculo Refugio de la Zaga podría llegar a ser lo suficientemente grande como para suponer una amenaza.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7554') AND (`Locale`='esES');
