UPDATE `npc_text_locale` SET `ID`='7746', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $c. $gBienvenido:Bienvenida; al Fuerte Cenarion. Desde aquí atacaremos a los silítidos cuando estén en su apogeo. Espero que hayas venido a echar una mano.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7746') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7747', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es un placer volver a verte, $N. Tu ayuda ha sido crucial para preservar la seguridad de nuestras líneas de abastecimiento. Espero que comprendas la importancia de la logística implicada en una gran batalla como esta.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7747') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7751', `Locale`='esES', `Text0_0`='¿Persigues a Pinzamorten, $N? Me dejas de piedra. He perdido a dos de mis hombres por culpa de ella solo en esta semana. Esperemos que tú corras mejor suerte que ellos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7751') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7752', `Locale`='esES', `Text0_0`='¡Eh! ¡Gracias por salvarme el pellejo! Creo que me voy a tumbar un rato para recuperar fuerzas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7752') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7753', `Locale`='esES', `Text0_0`='¿Quién eres? ¿Dónde estoy? Me siento... tan débil...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7753') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7754', `Locale`='esES', `Text0_0`='¿Qué bicho se presenta antes los Señores del Consejo? ¡Sin duda, uno que quiere morir aplastado!', `Text0_1`='¿Qué bicho se presenta antes los Señores del Consejo? ¡Sin duda, uno que quiere morir aplastado!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7754') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7755', `Locale`='esES', `Text0_0`='Es una larga historia así que es mejor que tomes asiento.$B$BCuando llegamos por primera vez a Silithus, la maestra Mar\'alith fue la primera de los elfos de la noche del Fuerte Cenarion en unirse a la expedición. Nuestro día a día era de lo más normal: un grupo se aventuraba en las diversas colmenas para buscar artefactos perdidos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7755') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7756', `Locale`='esES', `Text0_0`='¡Lo es! Esas colmenas estaban atestadas de silítidos que no se alegraban mucho de vernos.$B$BTodo marchaba bien hasta que nos topamos con un cristal jeroglífico en Colmen\'Regal. ¡La atrajo como si de un imán se tratase! Ella puso su bonita mejilla contra el cristal y al instante entró en un estado de trance. Naturalmente, todos estábamos muy preocupados. La cogimos y la sacamos de allí al momento.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7756') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7757', `Locale`='esES', `Text0_0`='La llevamos ante el Comandante con la esperanza de que los druidas del Círculo pudieran ayudarla. En efecto, fueron capaces de conseguir que despertara. Supusimos que ya no teníamos nada más que hacer en el Círculo y que podíamos retomar nuestro trabajo.$B$BPasaron varias semanas y todo iba bien. Brann aún andaba por allí así que el trabajo estaba bien estructurado. Y un buen día, de repente, ella apareció en nuestro campamento.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7757') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7758', `Locale`='esES', `Text0_0`='¿Eres $gtonto:tonta;? ¿De quién iba a estar hablando si no? Sí, la chica, la maestra Mar\'alith. En cualquier caso, ella estaba de vuelta y no de muy buen humor. Y entonces comenzaron las exigencias.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7758') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7759', `Locale`='esES', `Text0_0`='¡Exigía que volviéramos inmediatamente a Colmen\'Regal! ¡Qué descaro por su parte! Brann le dijo que era imposible volver a esas colmenas sin contar con el respaldo de un ejército. Se habían vuelto todavía más peligrosas. Esos apestosos insectos habían triplicado su número desde la primera vez que nos aventuramos en las colmenas.$B$B¡Y entonces fue cuando Natalia perdió la razón!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7759') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7760', `Locale`='esES', `Text0_0`='¿Qué quieres decir, qué quieres decir...? Pues eso, ¡que se volvió loca! Empezó a golpes con nosotros y, aunque yo no tengo por costumbre pegar a una chica, cuando desperté tras haberme desvanecido, estaba furioso con ella. ¡Era como si Brann, Rutgar, el mono y yo fuéramos orcos a ojos de ella! Es un milagro que fuéramos capaces de evitar su ataque. ¡Estaba como poseída!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7760') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7761', `Locale`='esES', `Text0_0`='¡Quién sabe! De cualquier modo, esa fue la última vez que la vi. Quizás Frankal pueda darte más datos.$B$BNo pretendo evitar este tema pues estoy seguro de que el trabajo que estás haciendo para el Comandante es importante, pero aquí estamos inmersos en algo muy grande. ¡REALMENTE GRANDE! Todas nuestras peticiones de un ejército a Forjaz hasta el momento han caído en saco roto. Parece ser que estamos en la obligación de proteger el reino u otra cosa sin sentido. Tras presentar tu informe ante el Comandante, regresa aquí con algunos amigos. ¡Necesitamos tu ayuda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7761') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7762', `Locale`='esES', `Text0_0`='Oh no, otra vez no... Siéntate $gmuchacho:muchacha;.$B$BEstoy seguro de que Rutgar ya te ha contado algo así que me ahorraré los detalles e iré directo al grano.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7762') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7763', `Locale`='esES', `Text0_0`='¡Estupendo! Pues la maestra había vuelto por segunda vez a las colmenas pero Brann no entendía nada de lo que estaba pasando. Antes de que reinara la confusión, ella empezó a hablar con algo o alguien. Y lo que es aún peor, empezó a salmodiar en un idioma extraño que ninguno de nosotros conocíamos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7763') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7764', `Locale`='esES', `Text0_0`='¡Y que lo digas! Sobre todo porque entre Brann, Rutgar, el mono y yo hablamos 38 idiomas, incluido un poco de titán, tanto vanir como aesir.$B$BEn todo caso, sea lo que fuere que decía, no hablaba con nadie que estuviera \"presente\". Después, asintió con la cabeza y pasó al ataque... ¡con todas sus fuerzas!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7764') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7765', `Locale`='esES', `Text0_0`='Mira $gchico:chica;, lo que nos atacó no era una simple sacerdotisa. ¡Sus ojos tenían un brillo maléfico y echaba espuma por la boca! Llegó, empezó a gritar a lo loco con lo que consiguió que cada uno de nosotros saliera corriendo en una dirección y finalmente empezó a destrozar nuestras mentes una a una. Yo empecé a oír palabras extrañas en mi cabeza y a ver cómo los demás perdían el conocimiento aquí y allá. ¡Era demencial! Hubiera sido nuestro fin de no ser por el condenado mono.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7765') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7766', `Locale`='esES', `Text0_0`='¿Preguntar qué? Es un mono, idiota. En cualquier caso, el mono se puso frenético al ver al viejo Brann caer desplomado sobre la arena. La elfa de la noche no podía hacer nada para detener a Glibb, que no paraba de darle golpes.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7766') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7767', `Locale`='esES', `Text0_0`='Y entonces... Ella salió corriendo directamente hacia la colmena. ¿Y sabes qué es lo más gracioso? Que ni un solo bicho de esos se interpuso en su camino o intentó detenerla. Corría entre ellos como si fueran amigos de toda la vida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7767') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7768', `Locale`='esES', `Text0_0`='Ajá.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7768') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7770', `Locale`='esES', `Text0_0`='El cristal está cubierto de complejos jeroglíficos y runas. No se entiende nada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7770') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7778', `Locale`='esES', `Text0_0`='¿Adónde quieres volar?', `Text0_1`='¿Adónde quieres volar?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7778') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7779', `Locale`='esES', `Text0_0`='Los espíritus de Silithus nos llaman, $N. La tierra llora pues tiene una triste historia que contar...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7779') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7780', `Locale`='esES', `Text0_0`='Ave $c. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.', `Text0_1`='Ave $c. ¿No te he visto hace poco en una reunión privada? Me suena tu cara pero esta ciudad está tan concurrida...$B$BSi has estado en esa reunión, te habrán dado un código secreto. Dime tu código y recibirás como recompensa un premio especial.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7780') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7781', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡$N! Espero que vengas para ponerte a trabajar, porque tienes mucho que hacer aquí, en la isla del Caminante del Sol.$B$BDesde la destrucción de la Fuente del Sol por Arthas y la Plaga, somos una raza a la deriva en un mar de incertidumbres. Nos balanceamos al filo del olvido. Esto cambiará, $c. Al mismo tiempo que aprendes, ayudarás a nuestra recuperación.$B$B¡Terrallende nos espera!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7781') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7782', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='El Círculo Cenarion es muy sabio y conoce muchos de los secretos de la tierra. Los artesanos harían bien en prestar atención a las palabras de estos druidas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7782') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7784', `Locale`='esES', `Text0_0`='Hubo un tiempo en que la Aguja del Sol disfrutó de una buena ventaja sobre La Fuente del Sol. Nuestras vidas han dado un vuelco, $N, pero aun así debemos continuar. Somos así; somos supervivientes.$B$BSi quieres sobrevivir a estos tiempos tumultuosos y seguir adelante, DEBES aprender a sobrevivir. Todos los elfos de sangre deben hacerlo. Debes dominar tu insaciable hambre de magia antes de que ella te domine a ti.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7784') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7785', `Locale`='esES', `Text0_0`='¡Tu continuo servicio a los elfos de sangre es crucial para nuestra supervivencia! Todos tenemos que ponernos manos a la obra para salir de este embrollo.$B$BSi estás $glisto:lista; para trabajar, empecemos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7785') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7786', `Locale`='esES', `Text0_0`='El conocimiento es poder; poder VERDADERO, joven aprendiz. Lo mejor que puedes hacer es acumular tanto como puedas y atender bien a quienes ya lo han hecho.$B$BAntes de arrasar La Fuente del Sol, nos engañábamos creyendo que habíamos alcanzado la cumbre de nuestra civilización. Necesitamos la Plaga para darnos cuenta... y, en cierto modo, volver a la realidad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7786') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7787', `Locale`='esES', `Text0_0`='¡Me has pillado en un momento crítico de mi investigación! No te preocupes, puedes unirte a mí en contemplación silenciosa... pero hago hincapié en \"silenciosa\", si no te importa.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7787') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7788', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Si te diriges a la ciudad, tengo algo importante que encomendarte.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7788') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7789', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡$GBienvenido:Bienvenida;! Adelante, no tengas vergüenza. Esto de aquí es mi mayor tesoro... ¡el Ultracañón Pimpampum 5000! ¿No es precioso? Parece que tienes un poco de barriga pero creo que te servirá. Volar es muy divertido. ¿Quieres aprender?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7789') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7790', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Oh, es muy sencillo! Se utiliza como cualquier otro aparato eléctrico. No te preocupes que yo me encargo de encender los fusibles. Cuando estés en el aire te parecerá que estás flotando pero esa sensación desaparece pronto. Tu objetivo en este caso es llegar hasta el lago que está allí a lo lejos. No te olvides de desplegar las alas en el momento oportuno antes de comenzar el descenso para aterrizar en el agua o de lo contrario... bueno, yo no vendo seguros de vida.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7790') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7792', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hola $c y $gbienvenido:bienvenida; a la Feria de la Luna Negra. Hay tanto que hacer aquí que puede resultar un tanto abrumador para aquellos que nos visitan por primera vez. Por eso estoy aquí para ayudarte. ¿Tienes alguna pregunta?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7792') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7793', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Excelente pregunta! Tenemos muchas cosas en esta feria. Si quieres comida o bebida, dirígete a la barraca de Stamp y Sylannia. Mi favorito es el Gran Reserva de la Luna Negra.$B$BSi por el contrario prefieres comprar algo más exótico, Lhara seguro que tiene lo que buscas pues su especialidad son las rarezas de todas las partes del mundo.$B$BY luego está Flik, que andará por ahí en alguna parte. Si lo encuentras verás que vende pelotas para lanzar y ranas para tener como mascotas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7793') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7794', `Locale`='esES', `Text0_0`='Me alegro de que me hagas esa pregunta. Los vales para la Feria de la Luna Negra son tu moneda de cambio para conseguir regalos increíbles.$B$BLos vales son la recompensa por hacer las misiones mensuales y jugar a los juegos de la feria.$B$BAl completar las misiones mensuales ganarás el mayor número de vales, pero si juegas a los juegos cada día, podrás ir adelantándote.$B$BCuando ya tengas suficientes vales, puedes gastarlo en los diferentes vendedores del recinto de la feria.', `Text0_1`='Me alegro de que me hagas esa pregunta. Los vales para la Feria de la Luna Negra son nuestro modo de darte la posibilidad de echar una mano en la feria y a cambio poder canjear tus vales por regalos especiales.$B$BPara empezar tendrás que hablar con Chronos, Kerri Hicks, Cristo y Yebb Nebblegear para decidir a quién quieres ayudar. Ellos siempre tienen algún trabajo por hacer que ayudará a mejorar nuestras atracciones.$B$BCuando ya tengas suficientes vales, habla con Gelvas Roñoso para que te dé la lista de todos los regalos disponibles.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7794') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7795', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Nadie sabe con certeza quién creó las cartas de la Luna Negra o cuál es su propósito real pero sí puedo decirte que las encontrarás por todo Azeroth, generalmente en manos de algún villano que desea desvelar sus misterios.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7795') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7796', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Te recomiendo que visites a Sayge para que te diga la buenaventura. Ya sé, ya sé... es un gnoll. Sí, pero es un gnoll muy especial que puede adivinar el futuro.$B$B¡Y continuamente ampliamos la feria con todo tipo de atracciones nuevas!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7796') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7797', `Locale`='esES', `Text0_0`='Tenemos más de un enemigo en Silithus, $N.$B$BMientras que todo el mundo centra su atención en los silítidos, mi hermano mayor y yo hemos estado vigilando de cerca a los chiflados del Martillo Crepuscular.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7797') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7800', `Locale`='esES', `Text0_0`='Saludos, $N', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7800') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7801', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $gviajero:viajera;. Fue aquí, hace casi 1000 años, donde los Qiraji comenzaron su oleada de destrucción por Kalimdor.$B$BSe perdieron miles de vidas durante la guerra, la mayoría de ellas en este desierto maldito durante la invasión inicial.$B$BYo soy una de los pocos que quedamos, atados tanto al pasado como al presente. No sé por qué tengo que permanecer aquí pero sé que no puedo hacer nada para evitarlo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7801') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7802', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Me han asesinado cien veces y siempre despierto de nuevo aquí. Algunos dicen que pesa una maldición sobre mí y otros...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7802') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7803', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Que se trata de una bendición: un heraldo en una nueva era.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7803') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7804', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hay fuerzas ancestrales acechando, fuerzas que no se habían percibido durante miles de años. El mundo llora, $r. Quizás se avecina un nuevo cataclismo. Un malestar que surge de dentro... $B$BEl mal está calando en nuestras tierras. Del Sueño salen pesadillas, que perturban nuestro despertar...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7804') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7806', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Feliz Halloween! ¿Te gusta mi máscara? ¡Da mucho miedo!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7806') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7812', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Qué NO se hace, querrás decir!$B$B¡Todos los taberneros del mundo regalan cosas ricas y muchas ciudades están adornadas para la ocasión! Además, se celebra todas las noches el Festival del Hombre de Mimbre en las afueras de Entrañas.$B$BUna cosa más: he oído que una niña enferma que está en el orfanato aquí en Orgrimmar busca a alguien que le ayude a conseguir caramelos durante esta fiesta... ¿Podrías ayudarle?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7812') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7816', `Locale`='esES', `Text0_0`='¡Márchate inmediatamente! No tengo nada que tratar contigo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7816') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7817', `Locale`='esES', `Text0_0`='¡Ya estás de vuelta! ¿Qué puedo hacer por ti en esta ocasión?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7817') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7818', `Locale`='esES', `Text0_0`='La guerra contra la Horda está en su apogeo, $N. Es un momento de gran necesidad para la Alianza y es necesario que cada uno de nosotros cumplamos con nuestro cometido.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7818') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7819', `Locale`='esES', `Text0_0`='La guerra se cierne sobre nosotros, $N. Es hora de que cada uno de los miembros de la Horda atienda la llamada y se una a la lucha contra la acobardada Alianza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7819') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7820', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Eres un peletero de oficio, ¿no es así? Es ese ligero aroma a pieles curtidas, lo reconocería en cualquier parte.$B$BMi padre solía hablar de un famoso artesano que vivió en la aldea del Viento del Sur siglos atrás. Es una lástima que el lugar ahora esté en ruinas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7820') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7839', `Locale`='esES', `Text0_0`='Me llegan continuamente noticias de tus proezas contra el Consejo Abisal, $N. ¡Estás haciendo grandes cosas ahí fuera!$B$B¡Sigue así y conseguiremos que el esfuerzo valga la pena!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7839') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7869', `Locale`='esES', `Text0_0`='$N, me alegro de volver a verte.$B$BRecuerda mantenerte centrado en tu aprendizaje. Tú representas nuestro futuro y, si vacilas, nuestro futuro será oscuro de verdad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7869') AND (`Locale`='esES');
