UPDATE `npc_text_locale` SET `ID`='7885', `Locale`='esES', `Text0_0`='¿Buscas el Fragmento de cetro encargado a la protección del Vuelo Azul?$B$B<Azuregos parece sonreír.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7885') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7886', `Locale`='esES', `Text0_0`='Por supuesto que no tengo ese fragmento. Eso sería una bestialidad. ¡Yo camino entre salvajes en esta tierra maldita! No puedo dar ni cinco pasos sin que algún loco orco o humano intente clavarme una lanza en el pellejo. En cualquier momento podrías estar hablando con mi espíritu fantasmal.$B$BComo sea, lo escondí.$B$BVamos... Pregúntame dónde.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7886') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7887', `Locale`='esES', `Text0_0`='Vas a adorar esta historia, $r. Mira, así va la cosa. Tan pronto como Malygos me encargó proteger el fragmento de cetro, supe que sería un eterno dolor de cabeza.$B$BTodo lo que quiero hacer es estudiar estas mágicas líneas costeras y hacerme con cualquier artefacto que pueda quedar del Hendimiento. Ya sabes, ¡Había una vez una próspera civilización Altonato aquí! No tienes que decirme que debe de haber muy buenos objetos mágicos por ahí, $r. ¡Lo puedo oler!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7887') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7888', `Locale`='esES', `Text0_0`='Como estaba diciendo, tenía en mi poder ese fragmento de cetro por unos quinientos años y no era más que problemas. Atraía todo tipo de atención... el tipo malo de atención. Quería que me dejen solo con mis estudios, no ser el boleto a la gloria de alguno que quisiera hacerse héroe. Así que... se lo dí a un pez.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7888') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7889', `Locale`='esES', `Text0_0`='No a cualquier pez, mortal. Este pez era un pececillo. Un pececillo muy especial.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7889') AND (`Locale`='esES');
