UPDATE `npc_text_locale` SET `ID`='7891', `Locale`='esES', `Text0_0`='Un genio, lo sé... Nunca será hallado. Pero, en caso de que sea encontrado, Le dí al pececillo unos poderes especiales.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7891') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7892', `Locale`='esES', `Text0_0`='Correcto. ¡Estabas escuchando! Este pececillo es realmente una terrible criatura de mi propia creación. Cuando estoy relajado me gusta pensar en los desafortunados pescadores que se encuentran con este pececillo y han sido devorados enteros. ¡Apuesto a que quedaron bastante sorprendidos!$B$B<Azuregos se ríe.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7892') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7893', `Locale`='esES', `Text0_0`='A menudo los genios son incomprendidos, $r. Ahora, sobre el fragmento de cetro... Si quieres ese fragmento y no deseas desperdiciar los próximos diez mil años buscándolo, tendrás que escuchar cuidadosamente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7893') AND (`Locale`='esES');
