UPDATE `npc_text_locale` SET `ID`='7895', `Locale`='esES', `Text0_0`='Bueno que... tienes que construir una boya de arcanita y ponerlo en el océano. Mi pececillo será atraído por las emanaciones mágicas. Cuando se acerque a la boya... ¡BOOM! Explotará en un glorioso pulso de energía arcana, revelando la verdadera forma del pececillo. Además, podrías incurrir en la ira de Neptulon, o quizás no. Yo diría que es un 50/50 de probabilidad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7895') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7896', `Locale`='esES', `Text0_0`='Si no te escuchara mejor, diría que estás burlándote de mí, mortal; pero sí, eso es mas o menos correcto. Y no tienes que quedar en gayumbos, puedes permanecer totalmente $gvestido:vestida;.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7896') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7897', `Locale`='esES', `Text0_0`='Lleva este libro de contabilidad a un viejo conocido mío en Tanaris. Su nombre es Narain Sabelotodo. ¡Un pésimo psíquico pero asombroso como ingeniero! Él debe poder hallarle sentido a todo esto.$B$BNo necesitas agradecermelo, $N. Es lo menos que puedo hacer.$B$B¡Buen día!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7897') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='7900', `Locale`='esES', `Text0_0`='Hace mil años, cuando la guerra del Mar de Dunas llegó a su fin, tres de los nuestros sacrificaron sus vidas y retuvieron a los qiraji en el interior de estos muros mientras Anacronos y Corzocelada sellaban Ahn\'Qiraj.$B$BTodos nosotros juramos volver a por Merithra, Arygos y Caelestrasz si algún día el Muro del Escarabajo se derrumbaba.$B$BY así fue... aquí estamos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7900') AND (`Locale`='esES');
