UPDATE `npc_text_locale` SET `ID`='7902', `Locale`='esES', `Text0_0`='<Narain coloca un sobre cerrado sobre su frente.>$B$BQué es... Mmmm... ¡Terciopelo!$B$B¿Tengo razón? $B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".', `Text0_1`=NULL, `Text1_0`='¡No me lo digas! ¡Shhh! ¿Estás aquí por la espada Crematoria? Sí... Sí... ¡No! Me he equivocado.$B$B¿En qué puedo ayudarte, $r?$B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".', `Text1_1`=NULL, `Text2_0`='¡Narain Sabelotodo, a su servicio! ¡Ingeniero retirado y maestro adivino! ¡Puedo adivinar tu futuro incluso antes de que ocurra!$B$BSi estás aquí para intentar volver a aprender un oficio, usa ese libro de ahí...$B$B<Narain señala \"Adivinar el futuro para torpes\".', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='7902') AND (`Locale`='esES');
