UPDATE `npc_text_locale` SET `ID`='8064', `Locale`='esES', `Text0_0`='No lo tomes a mal, prefiero no estar día tras día en un túnel lleno de silítidos, pero si el estar aquí escondidos observando los movimientos de estas criaturas nos va a ayudar a ganar la guerra, entonces estaré todo el tiempo que sea necesario.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8064') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8065', `Locale`='esES', `Text0_0`='Pronto libraremos a Azeroth de la abominable presencia de los silítidos. Su potencia destructiva es mucho mayor de lo que pensaste en un primer momento, $N. Poderes mayores y más oscuros se esconden tras sus prácticas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8065') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8076', `Locale`='esES', `Text0_0`='\"En el cuello de este reno se encuentra una marca con el logotipo de Pastos Bosquehumeante y un nombre: \"Metzen\"$B$BMetzen te mira con ojos de tristeza y desamparo. Parece como si algo, una fuerza oculta, hubiera inmovilizado al reno\".', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8076') AND (`Locale`='esES');
