UPDATE `npc_text_locale` SET `ID`='8222', `Locale`='esES', `Text0_0`='¡Me encanta el Festival Lunar! ¡Es la ocasión ideal para mover las patas!', `Text0_1`='¡Me encanta el Festival Lunar! ¡Es la ocasión ideal para mover las patas!', `Text1_0`='No sé exactamente cómo funcionan estos fuegos artificiales, ¡pero seguro que son geniales!', `Text1_1`='No sé exactamente cómo funcionan estos fuegos artificiales, ¡pero seguro que son geniales!', `Text2_0`='Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.', `Text2_1`='Esta luz lunar te llevará hasta Cima del Trueno. Utilízala cuando desees abandonar el festival.', `Text3_0`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `Text3_1`='No intentes teletransportarte a las ciudades de tus enemigos pues no funcionaría. Aquí todos somos muy amigos pero de vuelta en casa las viejas rencillas no se olvidan...', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8222') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8226', `Locale`='esES', `Text0_0`='Hemos trabajado muy duro para ver cómo nuestras tierras volvían a su antigua gloria, $c. Debemos estar siempre alerta contra aquellos que han invadido y profanado Quel\'thalas. No descansaremos hasta que estén fuera de nuestra vista.$B$B¡No dejemos que exista nadie que se interponga en el camino de los sin\'dorei!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8226') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8227', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es un caos, $c, y es todo por mi culpa. Me dijeron que saliera del muelle mientras tenía tiempo, pero no hice caso. No.$B$B¿Qué voy a hacer ahora? Esos malditos elfos de sangre, los desdichados, se han largado con todo mi material mágico y se lo han comido, creo. Estoy segura de que no lo recuperaré nunca. ¡Y los múrlocs Malaescama me robaron lo poco que quedaba en medio de la noche! Para rematar la faena, mi tripulación ha abandonado el barco.$B$BCreo que voy a llorar.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8227') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8228', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hola, $c. Estamos en tiempos verdaderamente preocupantes si esto es en lo que se va a convertir nuestra amada Quel\'Thalas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8228') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8229', `Locale`='esES', `Text0_0`='Los desdichados son una espina que tengo clavada, $N. Cuando no son un peligro para ellos mismos porque explotan fuentes Arcanas inestables para satisfacer sus antojos, traman ardides alocados contra nuestra respetable sociedad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8229') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8232', `Locale`='esES', `Text0_0`='Espero que Loralthalis esté bien, $c. Quizás debería subir a Aguja Ocaso Marchito yo mismo. ¿Quizás es lo que debería haber hecho en primer lugar?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8232') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8233', `Locale`='esES', `Text0_0`='Un hombre solo puede vivir consigo y su vergüenza durante tanto tiempo, $c, antes de que tenga que actuar para deshacer los errores que ha cometido.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8233') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8234', `Locale`='esES', `Text0_0`='¡Un desastre, eso es lo que es! ¡Todas esas criaturas han aparecido de la nada y corretean por todas partes, fuera de control! Y, ¿dónde se han metido el magister y sus aprendices? Me dejan aquí solo sin siquiera decir \"Hola, Wyllithen, ¿te quieres venir?\"$B$B¡Menuda cara!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8234') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8238', `Locale`='esES', `Text0_0`='¡Eh! Tienes pinta de ser $gun:una; $c con un paladar muy exigente para los vinos. Tengo que pedirte un favor y creo que no podrás rechazar la oferta que te voy a hacer... ¡Olvídalo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8238') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8239', `Locale`='esES', `Text0_0`='¿Qué estás mirando, $gmuchacho:muchacha;? ¿Es que nunca has visto un enano?$B$BSerá mejor que te pongas en camino si no quieres tener problemas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8239') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8240', `Locale`='esES', `Text0_0`='Ah, debes de ser $gel muchacho:la muchacha; $gdesignado:designada; para mostrarme los alrededores del bosque Canción Eterna. Te llevó tu tiempo llegar hasta aquí, ¿no es así?$B$BMuy bien, vámonos. No perdamos ni un minuto más.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8240') AND (`Locale`='esES');
