UPDATE `npc_text_locale` SET `ID`='8280', `Locale`='esES', `Text0_0`='La mansión Ravenholdt te da la bienvenida, $gaventurero:aventurera;.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8280') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8304', `Locale`='esES', `Text0_0`='Tenemos órdenes de arriba de acabar con Rajaxx. Causad daño extremo si queréis mejorar vuestra técnica.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8304') AND (`Locale`='esES');
