UPDATE `npc_text_locale` SET `ID`='8332', `Locale`='esES', `Text0_0`='Thrall ha sido muy generoso acogiéndome en su dominio. De ningún modo quisiera abusar de su hospitalidad. Causarle problemas a nuestro líder sería una gran deshonra para mí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8332') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8333', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Intenta no llamar mucho la atención. Estoy esperando a que el Rey me dé audiencia. No es que me importe esperar... de hecho, me siento bastante segura rodeada de todos estos fornidos guardias.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8333') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8335', `Locale`='esES', `Text0_0`='¡Saludos, $gamigo:amiga;! ¿Puedo ofrecerte un condensador ectoplásmico? ¿O quizás un fabuloso asimilador de espectros omnidireccional?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8335') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8336', `Locale`='esES', `Text0_0`='¿Y ahora qué? ¿No ves que estoy ocupado?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8336') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8344', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Estaré bien, no te preocupes por mí. Saldré de aquí cuando haya realizado algunos rituales de purificación en esta habitación. Este lugar está contaminado con magia negativa, $N.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8344') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8347', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Nuestras nuevas atracciones, totalmente gratuitas, o tonques que es el ingenioso nombre que les hemos puesto, se controlan mediante cualquiera de las consolas de control que se encuentran por toda la feria.$B$BTodo lo que tienes que hacer es activar cualquier consola de control que esté disponible. Cada tonque está totalmente equipado con una serie de artefactos y de manera aleatoria podrás descargar una fuerza de ataque especial contra tus enemigos.$B$BVive batallas épicas con tus amigos, pasea con tu tonque o provoca el caos. ¡Tú eliges!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8347') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8350', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Se comenta que Kel\'Thuzad ansía el legendario Atiesh, el gran báculo del guardián.$B$BSí, el mismísimo báculo que Medivh portó en su día.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8350') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8351', `Locale`='esES', `Text0_0`='Ten cuidado, $r. Los de la Hermandad de la Luz no somos tan indulgentes como nuestros hermanos cuando se trata de impartir justicia a aquellos que hacen el mal en el mundo.$B$B¡Ay de aquellos que provoquen la ira de Korfax!$B$BY ahora, si no deseas nada más de mí ni de los otros miembros de la Hermandad, haz el favor de dejarnos para que podamos proseguir con nuestra reunión.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8351') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8352', `Locale`='esES', `Text0_0`='El Rey Exánime intenta hacer sentir su presencia en nuestro mundo. No debemos permitir que esto ocurra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8352') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8353', `Locale`='esES', `Text0_0`='¿Qué es la Hermandad de la Luz? Bien... Todos nosotros somos miembros de El Alba Argenta pero, a diferencia de otros, carecemos de moralidad, culpabilidad y otras emociones humanas inútiles. Podría decirse que somos como un miembro de la Cruzada Escarlata pero sin estupidez, falta de liderazgo y fanatismo ciego.$B$BNo permitas que el Comandante Escarlata te diga lo contrario.$B$BY que Neófitus no sepa que te he dicho esto... Sería una pena que tuviera que mutilarlo brutalmente en caso de que se produjera una desafortunada reyerta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8353') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8354', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los de El Alba Argenta serán unos idealistas impotentes pero su ilusorio líder sabe que no son capaces de aguantar. Estoy aquí en calidad de emisaria de la Cruzada Escarlata. Una embajadora, si preferís... No hay duda de que no podríamos infligir ni el menor de los daños a los ejércitos de Kel\'Thuzad, unidos o no, pero debemos intentar contenerlos el mayor tiempo posible a la espera de que se produzca un milagro.$B$B¿En cuanto a mí? Moriré luchando...$B$B<Marjhan se encoge de hombros.>', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8354') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8355', `Locale`='esES', `Text0_0`='Mi título es engañoso. Cuando me conozcas bien te darás cuenta de que soy muy simpático...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8355') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8356', `Locale`='esES', `Text0_0`='Casi envían a mi hermano, Loksey, a esta reunión. No es capaz ni de cuidar a sus canes y mucho menos de representar de manera competente a la Cruzada Escarlata.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8356') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8357', `Locale`='esES', `Text0_0`='Estaba espe...$B$B<Rimblat tose.>$B$B¡Oh, perdona! Soy Rimblat del Anillo de la Tierra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8357') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8358', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hola, $c. Por favor, discúlpame. Estoy manteniendo una conversación con Rimblat. Él cree que la tierra todavía tiene salvación.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8358') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8359', `Locale`='esES', `Text0_0`='¿Qué es lo que quieres, roedor? Desaparece inmediatamente de mi vista si no quieres descubrir por qué me llaman el Colérico.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8359') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8366', `Locale`='esES', `Text0_0`='Soy lo que vosotros denomináis un capitalista, $r.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8366') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8374', `Locale`='esES', `Text0_0`='Oh, buenas... mmmm ¿cómo te llamas?$B$BNo importa, ¿qué te trae a mi hogar alejado del hogar, aquí en la bella campiña?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8374') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8375', `Locale`='esES', `Text0_0`='Hola, $c. Confío en que te lo estés pasando bien. ¿Qué opinas de la fiesta? Realmente espero que no se nos esté acabando nada. Nada mata una fiesta más rápido que quedarse sin bebidas y entremeses, ¿no crees?$B$BSí, bueno, tengo que ir a atender a mis otros invitados. Disfruta de tu estancia.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8375') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8379', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿No es divertidísimo? Es magnífico que Lord Saltheril organice estas fiestas todas las noches, especialmente con todo lo que está ocurriendo en el sur. La verdad es que una fiesta te permite olvidarlo todo, ¿verdad?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8379') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8388', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Al igual que Kel\'Thuzad, yo fui una maga del Kirin Tor. Y al igual que Kel\'Thuzad, busco el legendario Atiesh, el gran báculo del guardián. Por este motivo, nuestros destinos están unidos, $c.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8388') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8389', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La progenie de Aegwynn, el que sería el último guardián de Tirisfal... Medivh.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8389') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8390', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es una historia de leyenda que no te voy a contar por falta de tiempo y apetencia. Tan solo has de saber que Medivh fue el último de los guardianes. El gran báculo de Atiesh fue la piedra angular tanto de su poder como de su corrupción.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8390') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8391', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La corrupción de un titán caído. La corrupción del Destructor. Después de todo, el báculo fue forjado a partir de la semilla del odio. Eso es, por supuesto, lo que me ha traído hasta aquí...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8391') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8392', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Tras la caída de Karazhan, el Kirin Tor envió a un grupo de magos a buscar la torre y a hacerse con todos los objetos que tuvieran relevancia con la excusa de \"investigar.\" Fue entonces cuando se encontró el legendario báculo y se trajo de nuevo a Dalaran.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8392') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8393', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Lo que vino después fue una serie de trágicos acontecimientos que acabaron con la muerte de todos los magos del Kirin Tor que habían estado en contacto con Atiesh.$B$BSi te das cuenta, se trataba de un objeto que tenía sentimientos, cualidad que Sargeras le otorgó...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8393') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8394', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='<Angela asiente con la cabeza.>$B$BTras esta sucesión de muertes, los Seis decidieron encerrar el báculo tras unas barreras de magia impenetrables que alguien debía custodiar: el vigía del báculo. Yo ocuparía ese puesto.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8394') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8395', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No queda mucho que contar. Archimonde destruyó Dalaran casi por completo y el báculo se perdió. Se cuenta que podría haber sido hecho añicos por los propios mecanismos de seguridad de la barrera.$B$BLos hay que creen que el báculo fue la causa de la represalia de Archimonde.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8395') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8396', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Tras la caída de Dalaran, abandoné el Kirin Tor. Durante años busqué el báculo para poder liberarme de la labor que el Kirin Tor me había encomendado. Encontraría el báculo aunque me llevara toda una vida. Así es como entré a formar parte de El Alba Argenta y el motivo por el que estoy aquí.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8396') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8397', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Le he seguido la pista a los diversos trozos del báculo por todo este mundo y así descubrí que Kel\'Thuzad ansía reconstruirlo. Varios fragmentos obran ya en su poder, custodiados por sus tenientes más fieles.$B$BNadie puede imaginar lo importante que es que el gran báculo permanezca fuera del alcance de Kel\'Thuzad. Francamente, desconozco cuáles serían las consecuencias aparte, claro está, de una catástrofe de enormes proporciones.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8397') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8401', `Locale`='esES', `Text0_0`='Saludos, viajeros. Tengo orden de tratar a quien posea una llave de Karazhan como un invitado especial del Maestro. Hacedme todas las preguntas que quieras. ¿En qué puedo ayudaros?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8401') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8402', `Locale`='esES', `Text0_0`='Somos demasiado compasivos con los desdichados. Deberíamos clavarle una espada a cada uno de ellos.$B$BSu osadía en el fondeadero Vela del Sol es imperdonable.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8402') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8403', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Llevo todo el día matando descerebrados seres de la Plaga en La Cicatriz Muerta. Espero que vengas a echar una mano y no solo a entretenerme con tu charla.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8403') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8404', `Locale`='esES', `Text0_0`='Vigila por donde andas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8404') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8406', `Locale`='esES', `Text0_0`='¡Malditas sean todas esas alimañas! Supongo que no podrías ayudarme con esta situación, ¿verdad?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8406') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8409', `Locale`='esES', `Text0_0`='¿Eres $gtonto:tonta;? ¿Te has olvidado de las CRIATURAS ESQUELÉTICAS que ensombrecían el paisaje en tu camino hasta aquí?$B$B<Rohan suspira.>$B$BSi prefieres el aire libre, encontrarás criaturas esqueléticas por todas las Tierras de la Peste tanto del Este como del Oeste. Créeme, no puedes dejarlos escapar. Ellos no lo harán contigo si tienen la oportunidad.$B$BSi por el contrario prefieres las mazmorras, has de ir sin duda a Stratholme y Scholomance en busca de trozos de hueso.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8409') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8410', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Otro día maravilloso y lóbrego aquí en Tranquillien, ¿no te parece, $c?$B$B¿Qué puedo hacer por ti?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8410') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8412', `Locale`='esES', `Text0_0`='Hola, amigo. ¿En qué puedo ayudarte hoy?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8412') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8413', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Te diriges a Tranquillien, $c? ¿Hay algo que pueda hacer por ti?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8413') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8414', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Ah! Buena pregunta.$B$BSi prefieres ir de caza al aire libre, encontrarás elementales en las Tierras de la Peste, Azshara, Las Estepas Ardientes, Frondavil, Silithus, Cuna del Invierno e incluso en el Cráter de Un\'Goro.$B$BSi te gusta correr riesgos quizás prefieras ir a La Masacre. He oído que sus muros encierran muchos elementales. También puedes ir en busca de los elementales que trabajan para los enanos Hierro Negro de las Profundidades de Roca Negra.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8414') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8415', `Locale`='esES', `Text0_0`='¿Estás de broma? ¿De verdad no lo sabes?$B$BSi eres más bien cobarde y no te gusta mancharte mucho las manos, encontrarás fragmentos de hierro negro en las criaturas que habitan Las Estepas Ardientes y La Garganta de Fuego.$B$BSi tienes espíritu aventurero, en las mazmorras de Cumbre de Roca Negra y las Profundidades de Roca Negra hay gran número de fragmentos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8415') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8416', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Si eres de los que prefieren estar al aire libre, los antárboles de Frondavil y los azotadores de Un\'Goro son una excelente fuente de hojas.$B$BSi te gusta correr riesgos, La Masacre Este y Oeste albergan una cantidad casi ilimitada de hojas salvajes.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8416') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8417', `Locale`='esES', `Text0_0`='$C, mira lo que han hecho con nuestras queridas tierras. ¡La Plaga debe ser destruida!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8417') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8418', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Va a costar un poco, pero creo que podemos salvar la vida de este elfo de sangre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8418') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8419', `Locale`='esES', `Text0_0`='¿Quién eres? Siento... tanto... frío.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8419') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8421', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es vergonzoso que nosotros, los sin\'dorei, hayamos perdido tanto de lo que solíamos llamar hogar. No pasará mucho tiempo antes de que recobremos nuestra fuerza y recuperemos nuestras tierras del sur del control de la Plaga.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8421') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8422', `Locale`='esES', `Text0_0`='Estoy demasiado ocupado como para andarme con juegos, $r. ¡Dime qué quieres o desaparece de mi vista!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8422') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8423', `Locale`='esES', `Text0_0`='¡El Sumo Ejecutor y la Dama Aurífera me están haciendo esperar, pero no tengo tiempo para esto! ¡Mis amigos están ahí fuera muriendo, $c!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8423') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8424', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Envié a la forestal Salissa al sur al enclave y al forestal Lethvalin al oeste, a Tranquillien, hace más de un día! ¿Has visto a alguno de los dos ¿Eres la ayuda que debían enviar?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8424') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8425', `Locale`='esES', `Text0_0`='A veces siento que nunca volveré a ser un simple funcionario al servicio de la Dama Aurífera. No estoy hecho para la guerra.$B$BAfortunadamente están aquellos, como tú, que arden en deseos de ver la antigua belleza de Quel\'Thalas devuelta a ella. ¡Aplasta a la Plaga, $c! ¡Aplástalos dondequiera que puedas!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8425') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8428', `Locale`='esES', `Text0_0`='Me han informado de que pudiste ayudar a la forestal Valanna y al teniente en Zeb\'Sora.$B$BGracias, $c. Supongo que ahora debería encaminarme de vuelta al Enclave del Errante.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8428') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8429', `Locale`='esES', `Text0_0`='¿Qué te trae por aquí, $c?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8429') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8431', `Locale`='esES', `Text0_0`='Ah, hola, $r. Como puedes ver, todavía no tengo abierto para los clientes. Si me disculpas, tengo mucho que hacer.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8431') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8432', `Locale`='esES', `Text0_0`='Es un gran placer volver a verte, $c. No, en serio. Eres la razón por la que estoy haciendo dinero aquí rápidamente.$B$BBueno, ¿qué te puedo vender hoy?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8432') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8433', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Te doy la bienvenida a Tranquillien, $c. Debo disculparme por el estado en que la encuentras. Como ves, hemos tenido algunos problemas, pero los Renegados y su Lady Sylvanas Brisaveloz han sido lo suficientemente corteses como para prestarnos mucha ayuda en nuestro tiempo de necesidad.$B$BSi puedes echar una mano, no dudes en hacerlo. Todos podemos hacer uso de la ayuda que podamos en nuestra lucha contra la miríada de fuerzas formadas contra nosotros.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8433') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8439', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Es muy importante que averigüemos qué están tramando los elfos de la noche, $c. Centra todos tus esfuerzos en ese fin.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8439') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8441', `Locale`='esES', `Text0_0`='¿Sois vosotros los refuerzos? ¡Los elfos de la noche han sido vencidos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8441') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8442', `Locale`='esES', `Text0_0`='¡Menos mal que desactivaste el cristal lunar en An\'owyn cuando lo hiciste, $c! Ven cuando te apetezca ayudarme con mi investigación.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8442') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8445', `Locale`='esES', `Text0_0`='¿Cómo he de trabajar en estas condiciones? ¡Y con todos estos Renegados por aquí! ¡Cuánto me aflige ver a Tranquillien y Quel\'Thalas así!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8445') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8446', `Locale`='esES', `Text0_0`='Sí... ¿qué quieres, $r? ¿No ves que estoy ocupado?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8446') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8447', `Locale`='esES', `Text0_0`='¡Informa, $r! ¿Qué noticias importantes tienes? No te entretengas... La Dama Aurífera y yo estamos en plena discusión estratégica.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8447') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8448', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida; a la Capilla de la Esperanza de la Luz, $gforastero:forastera;.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8448') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8452', `Locale`='esES', `Text0_0`='Nuestras caravanas parten dos veces al día pero también es cierto que la Plaga les ha salido al encuentro para detener a Didier y sus mulas y así evitar que el envío llegue a las primeras líneas. Es lo más arriesgado que he visto en mi vida. Si contáramos con valientes que custodiaran los envíos...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8452') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8454', `Locale`='esES', `Text0_0`='¡Tengo mucho que hacer y poco tiempo para hacerlo, $r! Por favor, apártate de mi camino.', `Text0_1`='¡Tengo mucho que hacer y poco tiempo para hacerlo, $r! Por favor, apártate de mi camino.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8454') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8456', `Locale`='esES', `Text0_0`='<El elfo de sangre recupera rápidamente la conciencia cuando le viertes la pócima en la boca.>$B$BTe debo la vida, $c. Sin tu ayuda habría sucumbido a la dolorosa tortura de la Plaga.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8456') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8457', `Locale`='esES', `Text0_0`='<El elfo de sangre yace en el suelo, sumido en un letargo artificial.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8457') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8458', `Locale`='esES', `Text0_0`='Saludos, $r. Soy el embajador Penasol de los sin\'dorei, o elfos de sangre, como se nos suele llamar. Tengo grandes esperanzas de que el nuevo vínculo entre nosotros y la Horda nos sea mutuamente beneficioso.', `Text0_1`='Saludos, $r. Soy el embajador Penasol de los sin\'dorei, o elfos de sangre, como se nos suele llamar. Tengo grandes esperanzas de que el nuevo vínculo entre nosotros y la Horda nos sea mutuamente beneficioso.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8458') AND (`Locale`='esES');
