UPDATE `npc_text_locale` SET `ID`='8463', `Locale`='esES', `Text0_0`='<Varnis tose con violencia mientras el jarabe vigorizante hace efecto.>$B$BGracias por rescatarme. ¡Debo volver a Tranquillien enseguida!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8463') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8464', `Locale`='esES', `Text0_0`='Las corrientes de aire fresco que soplan desde Canción Eterna de vez en cuando realmente me afectan al estómago, ¿sabes lo que quiero decir?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8464') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8466', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Si lo que quieres explicar es un asunto de importancia, $c, tal vez sería más apropiado que pidieras una audiencia con la Dama Aurífera. Sin embargo, debo advertirte que está muy ocupada y tal vez tengas que esperar mucho.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8466') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8467', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Normalmente yo no debería hablar contigo directamente; lo normal sería que pidieras una audiencia a la señora. Sin embargo, te encargué personalmente una tarea, así que ¿qué querías comentar?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8467') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8469', `Locale`='esES', `Text0_0`='Este panel contiene varios anuncios y noticias sobre recompensas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8469') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8470', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Tenemos trabajo importante que hacer, $N. Ataquemos a nuestros enemigos mientras tengan la guardia baja. Enseñémosles que la venganza de los sin\'dorei no olvida ni perdona.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8470') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8472', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Vaya, eso es un problema! ¿Cómo se supone que debo obtener las muestras de metal para los magistri si las minas están inundadas con esa chusma gnoll?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8472') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8474', `Locale`='esES', `Text0_0`='¿Dónde podría estar? Envié a mi aprendiz, Shatharia, a las Minas Sondaluz, al oeste, hace algún tiempo. ¡Necesitamos recuperar esas muestras de mena, y rápido, o nuestro experimento fracasará!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8474') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8475', `Locale`='esES', `Text0_0`='Sin la entrega de la mena de Sondaluz de una forma tan oportuna, nuestra investigación habría fracasado.$B$BEspera a que Shatharia vuelva a asomar la cabeza por aquí. ¡Tengo unas cuantas tareas interesantes para ella, puedes estar $gseguro:segura;!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8475') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8476', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los sin\'dorei hemos perdido demasiado a lo largo de los últimos años. Nuestras tierras, nuestras casas, hasta nuestras identidades... $B$BYa va siendo hora de que empecemos a recuperarlo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8476') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8477', `Locale`='esES', `Text0_0`='Antes de que podamos superar obstáculos externos, como la Plaga y los trols Amani, debemos superar obstáculos internos. Los desdichados han sido una molestia durante el tiempo suficiente; es hora de derrotarlos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8477') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8478', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Crees que podrías ayudarme a salir de aquí?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8478') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8479', `Locale`='esES', `Text0_0`='Entre los diversos fragmentos de pergamino que contienen listas de verificación y otra información de los Errantes, destaca uno. ¡Es el dibujo de un trol horripilante, quizás la criatura más espantosa que hayas visto!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8479') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8480', `Locale`='esES', `Text0_0`='En efecto, las colinas de Cuna del Invierno están en este momento intentando resistir un ataque renovado de la Plaga. Tu asistencia les sería de gran ayuda.$B$BNúmero restante de Necrópolis: $2284W', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`='0', `Text7_1`=NULL WHERE (`ID`='8480') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8484', `Locale`='esES', `Text0_0`='Si no se envía pronto apoyo adicional a las Estepas Ardientes, me temo que la Plaga establecerá una base de operaciones allí. Si puedes, deberías ir allí a ayudar a los defensores.$B$BNúmero restante de Necrópolis: $2281W', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`='0', `Text7_1`=NULL WHERE (`ID`='8484') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8485', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Se pueden obtener muchas ventajas conociendo al enemigo. Apréndete esa lección, $N.$B$BCuanto más sepamos sobre la Plaga, más bien preparados estaremos para derrotarlos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8485') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8487', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida; al Enclave del Errante, $c. Quédate todo el tiempo que quieras. Son bienvenidos todos los que nos apoyan en las Tierras Fantasma.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8487') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8488', `Locale`='esES', `Text0_0`='Las fuerzas Arcanas perdidas pueden hacer cosas raras a la tierra, a los elementos e incluso a los espíritus de los muertos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8488') AND (`Locale`='esES');
