UPDATE `npc_text_locale` SET `ID`='8491', `Locale`='esES', `Text0_0`='¡Me alegro de volver a verte, $gquerido:querida;! Formamos un buen equipo. Ya nos veremos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8491') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8495', `Locale`='esES', `Text0_0`='Tu ayuda con los trols Sombrapino está siendo indispensable, $c. Sigue así y tal vez podamos volver a ocuparnos de la Plaga.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8495') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8496', `Locale`='esES', `Text0_0`='Has asestado el golpe decisivo contra los trols Sombrapino, $N. Solo espero que podamos reunir nuestras fuerzas lo suficientemente rápido como para rematarlos antes de que reciban refuerzos de Zul\'Aman.$B$BEstaría bien que Tranquillien nos proporcionara refuerzos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8496') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8498', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Ciertamente, $N. Sólo tienes que decirme de que reputación quieres conocerlo.$B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8498') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8499', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Ciertamente, $N.$B$BLos aventureros que el Alba ve como Venerados deben presentar en conjunto un total de 14 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requeriría presentar un total de 90 insignias.$B$B¡Los que son Venerados por el Alba encuentra el costo de los artículos superiores bastante razonable! El costo de los artículos más poderosos sigue siendo alto. $B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8499') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8500', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Ciertamente, $N.$B$BLos aventureros que el Alba ve como Honorables deben presentar en conjunto un total de 40 insignias para acceder a artículos superiores. Para tener acceso a los artículos de mayor poder aún, se les requerirá presentar un total de 150 insignias.$B$BDebo admitir, que le precio es alto incluso para ellos. $B', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8500') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8501', `Locale`='esES', `Text0_0`='La situación es más desalentadora de lo que se ha hecho creer a muchos. Los Renegados están aquí como aliados en la lucha contra nuestro enemigo común, la Plaga.$B$B¡Confía en mí cuando digo que sin ellos, las Tierras Fantasma estarían perdidas y la Plaga ya habría invadido todo el Bosque Canción Eterna!$B$B¡Pero, más que nada en el mundo, necesitamos tu ayuda para recobrar nuestras antaño gloriosas tierras!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8501') AND (`Locale`='esES');
