UPDATE `npc_text_locale` SET `ID`='8504', `Locale`='esES', `Text0_0`='Estas piedras rúnicas protegen las tierras de Canción Eterna de ser contaminadas por la Plaga. La piedra rúnica del oeste fracasó hace mucho, forzándonos a quemar la tierra que había a su alrededor.$B$BMientras viva, no permitiré que las piedras rúnicas restantes nos fallen. Tal es mi juramento.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8504') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8505', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Bueno, no has venido hasta aquí para parlotear con una taza de té en la mano. Veamos cómo podrías ayudarnos a salir de esta situación.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8505') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8506', `Locale`='esES', `Text0_0`='Hay quien dice que la Cruzada Argenta es una reliquia del pasado. Yo soy uno de ellos, pero no porque la Plaga haya caído.$B$BNo... Es por su tozudez. Se han convertido en algo demasiado parecido a la iglesia de la que se escindieron: demasiado rígidos, demasiado sometidos al honor y al orden. Es lo que intento decirle a Barthalomew, en cualquier caso.$B$BSé que suena a demagogia, pero queda bien en los debates.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8506') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8513', `Locale`='esES', `Text0_0`='El maestro artesano Omarion simplemente desapareció una noche, como un susurro al viento... ¡Paf! ¡Y desapareció! Ahora soy el único que puede fabricar los objetos para los chicos que están en primera línea de batalla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8513') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8515', `Locale`='esES', `Text0_0`='Los sin\'dorei y los Renegados comparten su objetivo en las Tierras Fantasma, $N. Juntos acabaremos con la Plaga y quién sabe... tal vez los Renegados sean valiosos aliados en nuestro intento de alcanzar Terrallende.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8515') AND (`Locale`='esES');
