UPDATE `npc_text_locale` SET `ID`='8658', `Locale`='esES', `Text0_0`='Me llamo Sid. Me alegro de ver alguna cara nueva por aquí. ¿Qué puedo hacer por ti?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8658') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8659', `Locale`='esES', `Text0_0`='Aunque hemos llegado a la tierra prometida, $N, aún queda mucho por hacer. Nuestra gente no llegará a Kael\'thas si nos lanzamos a la desesperada a través de Terrallende.$B$BDebemos encontrar rutas seguras, asegurarnos los recursos y esquivar o destruir a nuestros enemigos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8659') AND (`Locale`='esES');
