UPDATE `npc_text_locale` SET `ID`='8667', `Locale`='esES', `Text0_0`='¡Me alegra volver a verte y que te encuentras bien, $N!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8667') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8672', `Locale`='esES', `Text0_0`='Mirren y yo somos dos de los mejores cazadores de cada lado del Portal Oscuro. Quédate, puede que aprendas un par de cosas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8672') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8673', `Locale`='esES', `Text0_0`='Espero que no busques cerveza. ¡No vamos a compartirla y basta!$B$BNo es culpa nuestra que a nadie se le ocurriera traer cerveza a Terrallende excepto a nosotros.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8673') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8674', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Por la esperanza de que no toda Terrallende sea tan inhóspita como esta región. Este es un cambio bastante grande de las verdes praderas de Canción Eterna.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8674') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8675', `Locale`='esES', `Text0_0`='Hay en Terrallende presencias demoníacas mucho más poderosas que nada que hayamos visto en el viejo mundo.$B$BHabrá un gran poder para aquellos que no las rehúyan, sino que aprendan a aprovechar y llegar a dominar su fuerza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8675') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8676', `Locale`='esES', `Text0_0`='Nací aquí, $r. Este es mi mundo – todo lo que conozco... De niño, mi madre y mi padre me obsequiaban historias de tu mundo y de su esplendor. Cómo ansiaba dejar este lugar.$B$BDesgraciadamente, el destino es un amante cruel. Mi madre y mi padre fallecieron hace tiempo y están enterrados en este mismo cementerio. Yo permanezco para defender su honor y el honor de mi familia – los hijos de Lothar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8676') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8678', `Locale`='esES', `Text0_0`='¿Qué puedo hacer por ti?', `Text0_1`='¿Qué puedo hacer por ti?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8678') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8680', `Locale`='esES', `Text0_0`='Terrallende rezuma energía mágica, está en el mismo aire. ¿La sientes, $N? No se me ocurre un lugar más apropiado para nuestra raza. ¡Pronto los sin\'dorei tendrán una nueva tierra de la que dirán que es su hogar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8680') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8681', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mi marido lleva días desaparecido. Nos fuimos juntos de peregrinación y se suponía que deberíamos estar de vuelta juntos también.$B$BEsta angustia me ha hecho abusar un poco de los cristales de maná.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8681') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8682', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='En realidad, los dracohalcones, si se crían adecuadamente, son bastante mansos. ¿No te parece fascinante? Ser tan agradables y sociables si se crían bien y tan fieros y letales en si hábitat natural... Los adoro con locura.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8682') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8683', `Locale`='esES', `Text0_0`='No... te preocupes... por... mí', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8683') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8684', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La verdad es que no creo que nuestra situación sea tan mala. Estoy segura de que todo se rectificará pronto y el poder de La Fuente del Sol se restablecerá. No es posible que sea tan malo como todo el mundo pretende que es.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8684') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8685', `Locale`='esES', `Text0_0`='Ha valido la pena hacerse amigo de los Errantes; yo puedo llevar a cabo investigaciones con sujetos que solo puedo encontrar en Terrallende y ellos... disfrutan de mi encantadora personalidad y de mi atractiva y dura apariencia.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8685') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8688', `Locale`='esES', `Text0_0`='Era solo un bebé cuando mi padre fue destinado a este páramo. Todo lo que siempre he sabido de él es lo que otros me han contado.$B$B¿Cambian tus sueños, $N? Los míos, no. Tengo un sueño: un horizonte carmesí me envuelve mientras la Legión, en cantidad innumerable, batalla en la distancia. Me arrodillo ante el cadáver de un hombre, es de suponer que mi padre, y lloro. Mientras lucha por conseguir aire, con todo el cuerpo aplastado, susurra algo. A pesar de todos mis esfuerzos, soy incapaz de oír lo que intenta decirme.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8688') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8698', `Locale`='esES', `Text0_0`='¿Crees que estoy siendo cruel con este gusano?$B$B¡Han matado a dos de mis hombres este mes! Somos demasiado amables con esta escoria de los desdichados.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8698') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8699', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida; a la aldea Brisa Pura. Lunargenta prácticamente nos ha abandonado, pero nos negamos a marcharnos. Este es nuestro hogar.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8699') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8700', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mi trabajo consiste en una única tarea: asegurar que el Sagrario del Oeste no tenga problemas.$B$BTendrás que disculpar mi temperamento, pero, como puedes ver, la situación se nos ha ido un poco de las manos.', `Text1_0`=NULL, `Text1_1`='¿Podría haber elfos de la noche saboteadores tras esto? Lo dudo. Con la carga que hemos estado poniendo en el Sagrario del Oeste solo era cuestión de tiempo que algo fuera mal.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8700') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8703', `Locale`='esES', `Text0_0`='¿Estás disfrutando del festival, $c?$B$BMientras los celadores de las llamas se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `Text0_1`='¿Estás disfrutando del festival, $c?$B$BMientras los celadores de las llamas se ocupan de las hogueras del presente, yo soy más bien un historiador y me interesan mucho los festivales anteriores. Por supuesto, estoy documentando las celebraciones de este año. De momento, todo está saliendo bien, ¿no crees?$B$B¿Sabes, $N? Hay un poder inherente a todas las hogueras del festival que se prenderán durante estas fiestas. Nosotros cuidamos las nuestras pero estoy seguro de que en las ciudades enemigas arden hogueras sagradas...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8703') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8712', `Locale`='esES', `Text0_0`='Si buscas trabajo, $c, agarra un pico y ponte a cavar. ¡Hay ruinas hasta donde alcanza la vista!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8712') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8713', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $N. Mis compañeros de los Hijos de Lothar y yo llevamos casi veinte años atrapados en este deprimente mundo. Hemos aguantado lo mejor que hemos podido, pero es asombroso ver que los peligros a los que nos enfrentamos hoy no son más que nuestros fracasos pasados.$B$B¿Nos ayudarás tú a superarlos?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8713') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8715', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La corrupción de Terrallende llega a extremos increíbles, $c. El Círculo Cenarion intenta comprender cómo han podido sobrevivir esas criaturas. El estudio de esta tierra desolada y maldita puede ayudarnos a encontrar la forma de curar algunas partes de nuestro propio mundo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8715') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8716', `Locale`='esES', `Text0_0`='Es triste ver lo que le ha pasado a esta tierra. La expedición de Cenarion hará lo que pueda para comprender el daño que se ha causado y repararlo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8716') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8718', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hola, $c. Tu llegada a Thrallmar no podría haber ocurrido en mejor momento. El Jefe de Guerra cuenta con nosotros, ya que no puede estar aquí él mismo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8718') AND (`Locale`='esES');
