UPDATE `npc_text_locale` SET `ID`='8740', `Locale`='esES', `Text0_0`='He oído hablar de tus hazañas, $N. Sé que, mientras estés en tierras de los Renegados te comportarás con dignidad y como un auténtico héroe de los sin\'dorei.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8740') AND (`Locale`='esES');
