UPDATE `npc_text_locale` SET `ID`='8751', `Locale`='esES', `Text0_0`='No tengo tiempo para cháchara, $c. Debo ocuparme de los peregrinos y asegurarme de que están preparados para el largo viaje que hay por delante.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8751') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8752', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; a la Avanzada del Halcón, $c. Yo me hago cargo de las necesidades de los peregrinos y los ayudo a continuar su largo viaje. Si todavía no has emprendido la peregrinación, puedo proporcionarte la información que necesites.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8752') AND (`Locale`='esES');
