UPDATE `npc_text_locale` SET `ID`='8755', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida; al Retiro del Errante. Somos forestales que hemos jurado defender a nuestra gente de los enemigos cercanos y lejanos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8755') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8759', `Locale`='esES', `Text0_0`='Oh... ¿nos conocemos?', `Text0_1`='Oh... ¿nos conocemos?', `Text1_0`='Lo siento, no hablo con el servicio.', `Text1_1`='Lo siento, no hablo con el servicio.', `Text2_0`='¿Y tú eres...?', `Text2_1`='¿Y tú eres...?', `Text3_0`='Creo que me has confundido con alguien que quiere hablar contigo.', `Text3_1`='Creo que me has confundido con alguien que quiere hablar contigo.', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8759') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8760', `Locale`='esES', `Text0_0`='¡Hola, $N! ¿Disfrutando de la fiesta?', `Text0_1`='¡Hola, $N! ¿Disfrutando de la fiesta?', `Text1_0`='Eh todos, ¡Est$ge:a; es $N!', `Text1_1`='Eh todos, ¡Est$ge:a; es $N!', `Text2_0`='Las palabras viajan rápido en este círculo. ¡Gracias por reabastecer los suministros!', `Text2_1`='Las palabras viajan rápido en este círculo. ¡Gracias por reabastecer los suministros!', `Text3_0`='¡Esta fiesta es la mejor! ¿No te parece?', `Text3_1`='¡Esta fiesta es la mejor! ¿No te parece?', `Text4_0`='Deberías probar los aperitivos de garragil, ¡son para morirse!', `Text4_1`='Deberías probar los aperitivos de garragil, ¡son para morirse!', `Text5_0`='Mmm... Me parece que la Reserva especial Toquesol de este año no es tan especial despues de todo. ¿No lo crees, $N?', `Text5_1`='Mmm... Me parece que la Reserva especial Toquesol de este año no es tan especial despues de todo. ¿No lo crees, $N?', `Text6_0`='Oí el rumor de que esta noche todos iremos a mirar la batalla con la Plaga en la frontera. ¡Qué deliciosamente perverso!', `Text6_1`='Oí el rumor de que esta noche todos iremos a mirar la batalla con la Plaga en la frontera. ¡Qué deliciosamente perverso!', `Text7_0`='¿Puedes creer lo que hizo esa Elisara Golpesol? Y pensar que es capaz de sacarle los ojos a Lord Saltheril. ¿Ves lo que está vistiendo? Se equivocó de estación. Que triste en verdad.', `Text7_1`='¿Puedes creer lo que hizo esa Elisara Golpesol? Y pensar que es capaz de sacarle los ojos a Lord Saltheril. ¿Ves lo que está vistiendo? Se equivocó de estación. Que triste en verdad.' WHERE (`ID`='8760') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8767', `Locale`='esES', `Text0_0`='Me he metido en una buena. La clarividente Regulkult intentó avisarme, pero mi descuido ha terminado por pasarme factura.$B$BBajé aquí para analizar las ruinas de Zeth\'Gor, pero me impliqué con la Legión en mi camino. Ahora todo lo que he logrado es una herida profunda en la pierna.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8767') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8768', `Locale`='esES', `Text0_0`='Aunque no puedo ofrecerte instrucción, joven $c, puedo darte mi bendición. ¡Camina con valentía hacia la Luz!$B$B¿Hay algo más?', `Text0_1`='Aunque no puedo ofrecerte instrucción, joven $c, puedo darte mi bendición. ¡Camina con valentía hacia la Luz!$B$B¿Hay algo más?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8768') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8769', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Es muy importante que averigüemos qué traman los elfos de la noche, $c! Centra todos tus esfuerzos en ese objetivo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8769') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8770', `Locale`='esES', `Text0_0`='No hace mucho, en este sitio tuvo lugar una terrible batalla. Nosotros, Nethergarde, y El Alba Argenta mantuvimos a raya una oleada de demonios que surgieron del Portal Oscuro.$B$BAquellos eran días oscuros llenos de sangre y desesperación...$B$BPero los reinos de Azeroth se unieron a la batalla y conseguimos expulsar a los demonios. La lucha continúa del otro lado del Portal...$B$BEste campamento ahora es un lugar tranquilo; por desgracia, esa tranquilidad contrasta con los horrores que hay del otro lado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8770') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8771', `Locale`='esES', `Text0_0`='Las heroicidades de la estirpe de los Aterratrols son legendarias. Idolatro a ese hombre.$B$BY, encontrarlo vivo, en Terrallende...$B$B<Romus mira fijamente más allá de ti.>$B$BCuando lo vi por primera vez, casi me desmayé. La conmoción fue insoportable. Mientras me arrodillaba ante él, tuve que reprimir las lágrimas.$B$BPuedes apostar tu dulce trasero a que voy a hacer todo lo que esté en mi poder para asegurarme de que los Hijos de Lothar se mantienen a salvo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8771') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8772', `Locale`='esES', `Text0_0`='Romus me ha informado de la tragedia que sufrió mi hermosa ciudad. No hay palabras que describan la pena que sentí cuando supe lo que le había ocurrido a Lordaeron y a mi amada Stromgarde.$B$BHace veinte años, nadie había oído hablar de la Legión Ardiente... ¡Malditos demonios!$BTe lo aseguro, $N; si algún día vuelvo a Azeroth, haré que Stromgarde recupere toda su antigua gloria. Lo juro...', `Text0_1`='Romus me ha informado de la tragedia que sufrió mi hermosa ciudad. No hay palabras que describan la pena que sentí cuando supe lo que le había ocurrido a Lordaeron y a mi amada Stromgarde.$B$BHace veinte años, nadie había oído hablar de la Legión Ardiente... ¡Malditos demonios!$BTe lo aseguro, $N; si algún día vuelvo a Azeroth, haré que Stromgarde recupere toda su antigua gloria. Lo juro...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8772') AND (`Locale`='esES');
