UPDATE `npc_text_locale` SET `ID`='8851', `Locale`='esES', `Text0_0`='Que la Luz te acompañe, $c.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8851') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8852', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Parece que por fin ha llegado la ayuda! Los omenai te damos la bienvenida a los restos de nuestro campamento, $N.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8852') AND (`Locale`='esES');
