UPDATE `npc_text_locale` SET `ID`='8854', `Locale`='esES', `Text0_0`='¡Llevará su tiempo, pero estos Tábidos aprenderán los caminos de la Luz!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8854') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8855', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida;, $N. Soy Landro Tirolargo, patrón de la Llama Negra. Puede que sea la primera vez que oyes hablar de mi local. No me sorprende; hacemos todo lo posible para que no se hable demasiado de nosotros. Hay que evitar meterse en líos entre las facciones.$B$BEn la Llama Negra nos especializamos en ofrecer los mejores servicios y el mejor entretenimiento que el dinero puede comprar; desde objetos difíciles de encontrar hasta peleas de gladiadores, pasando por lucrativos juegos de azar.$B$B¿Buscas algo en particular?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8855') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8860', `Locale`='esES', `Text0_0`='Las Tierras Fantasma no se recuperarán con solo la fuerza militar, $N. Aquí es donde entran en juego los magistri.$B$BNuestra astucia y nuestro conocimiento de lo Arcano no tienen igual.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8860') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8861', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='<La sacerdotisa Il\'dinare te mira con recelo.>', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8861') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8862', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Soy una elfa de la noche, $gextranjero:extranjera;. Aunque puede que no sepas nada de mi especie, nosotros sabemos mucho de ti y de tus progenitores. Puede que no te llames Eredar, pero, en apariencia, compartís muchas similitudes.$B$BHas ayudado a mis aliados, y te estoy agradecida por ello; pero, por el momento, andas por un camino incierto. Quizás con el tiempo puedas disipar las dudas y temores que albergo en el corazón.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8862') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8864', `Locale`='esES', `Text0_0`='Ándate con cuidado, forastero.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8864') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8865', `Locale`='esES', `Text0_0`='Has sido de gran ayuda hasta ahora, $N. Harías bien en ayudar a los demás en el campamento.$B$BY no te preocupes por esos goblins, me estoy haciendo cargo de todo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8865') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8867', `Locale`='esES', `Text0_0`='¿Qué miras?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8867') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8868', `Locale`='esES', `Text0_0`='Soy Adamant Coracero, de la gran ciudad de Forjaz. Como probablemente puedas suponer viendo mi uniforme, una vez formé parte de la guardia de élite de Forjaz. ¡Sí, fui la mano derecha de Magni! Al menos, hasta que me retiré. Ahora me intereso por la arqueología. En realidad es una afición; además, ¡pensé que sería una gran forma de ver mundo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8868') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8869', `Locale`='esES', `Text0_0`='<Cowlen solloza.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8869') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8870', `Locale`='esES', `Text0_0`='¿Qué puedo haber hecho para dar a los dioses motivos para arremeter contra mí con tal ferocidad? Todo lo que quería ha desaparecido – muerto o ha sido destruido. Mi familia, arrebatada por las bestias del bosque. Mi hogar, arrasado. Mi nave, hundida.$B$BSoy un hombre roto, perdido en una isla rota.$B$B¿Quizás estás aquí para terminar el trabajo? Hazlo rápido, no opondré resistencia. Toma mi vida, $gextranjero:extranjera;; soy demasiado cobarde como para hacerlo yo mismo. Mi mano tiembla... el filo no será certero.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8870') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8871', `Locale`='esES', `Text0_0`='Si no los dioses, ¿quién? ¿Cómo? ¿Qué podría causar tal disputa?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8871') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8872', `Locale`='esES', `Text0_0`='¿Legión? ¿Demonios? ¿Te dan caza? Mi pueblo también ha sido perseguido por la Legión: blanco de sus maestros oscuros para la condenación.$B$B<Cowlen se seca las lágrimas.>$B$BEsa es la razón por la que trasladé a mi familia aquí hace tantos años.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8872') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8873', `Locale`='esES', `Text0_0`='Saludos, $c. Antes de que los elfos de la noche invitaran a los draenei a unirse a ellos aquí, este claro estaba casi olvidado.$B$BLos kaldorei tienen un profundo vínculo con estas tierras, y nosotros, los draenei, nos sentimos honrados de compartir la tarea de revitalizar Canción del Bosque.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8873') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8874', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Me alegro de ver una cara nueva por aquí. Nuestra Avanzada es pequeña y nuestros enemigos, muy numerosos. Estamos rodeados por seres que quieren eliminarnos: sátiros al oeste, orcos del aserradero Grito de Guerra al sur y demonios del Cerro Lumbrevil.$B$BEs un alivio que los draenei se hayan unido a nosotros para reforzar Canción del Bosque contra todos esos peligros.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8874') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8875', `Locale`='esES', `Text0_0`='Me alegra volver a oír las voces de otros aquí, en Canción del Bosque. Me había empezado a sentir como si fuera el último de los kaldorei que fuera a poner la vista en estas tierras antes de su descenso a la corrupción.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8875') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8876', `Locale`='esES', `Text0_0`='¿Sientes el mal al sur? ¡Por la Mano, debe ser purificado!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8876') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8878', `Locale`='esES', `Text0_0`='Aunque has asestado un duro golpe al mal demoníaco del sur, sigo sintiendo problemas en esa región.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8878') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8883', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hay demonios que matar, $c. ¿Tienes agallas para hacer lo que hay que hacer?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8883') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8887', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Con una buena parte de sus demonios asesinados, la Legión se lo pensará dos veces antes de volver a atacar nuestro aserradero. Sin embargo, aún siento cómo despierta un mal demoníaco.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8887') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='8892', `Locale`='esES', `Text0_0`='¡Saludad todos $gal héroe exaltado:a la heroína exaltada;!', `Text0_1`=NULL, `Text1_0`='¡La historia de nuestro viaje será narrada en años venideros!', `Text1_1`=NULL, `Text2_0`='Por fin tengo una historia que contarle a mis nietos... ¡Un traidor del reino estaba viviendo entre nosotros!', `Text2_1`=NULL, `Text3_0`='A decir verdad, nunca me gustó ese gnomo...', `Text3_1`=NULL, `Text4_0`='Tenemos suerte de haber sobrevivido a ese calvario. ¡De no ser por ti, todas las personas de esta isla podrían haber muerto!', `Text4_1`=NULL, `Text5_0`='Me pregunto cuántos quedarán entre nosotros. Conocía a Chispik desde hacía muchos años. Nunca habría imaginado que era un traidor.', `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='8892') AND (`Locale`='esES');
