UPDATE `npc_text_locale` SET `ID`='9054', `Locale`='esES', `Text0_0`='¡Shh! Silencio absoluto; estoy observando a los múrlocs.$B$BVivir en un caparazón de tortuga gigante es un sacrificio que estoy dispuesto a hacer por la ciencia. Después de todo, si los múrlocs me ven aquí, ¡reaccionarán ante mi presencia y contaminarán mis datos!$B$B¡Como el primer gnómico de la Liga de Expedicionarios, voy con valentía a lugares a los que ningún enano ha ido antes!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9054') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9061', `Locale`='esES', `Text0_0`='Los alumnos son un poco como ovejas. Son útiles y hasta agradables cuando los orientas y les prestas mucha atención.$B$BLos dejas solos un minuto y acaban cayéndose por un precipicio o ahogándose en un río poco profundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9061') AND (`Locale`='esES');
