UPDATE `npc_text_locale` SET `ID`='9085', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Oh, no hagas caso de Buddy. No te hará daño, solo le gusta arañar de vez en cuando.$B$BEs solo un gatito grande y adorable.$B$B¿No es así, Buddy? Sí, bonito, cosita. ¡El pequeñín de tu mami, eso es lo que eres!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9085') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9086', `Locale`='esES', `Text0_0`='Nosotros, los druidas de Cenarius, protegemos el equilibrio de la naturaleza. Lo más importante es el equilibrio. En momentos de necesidad, no deberíamos tardar mucho en tomar acciones decisivas. Que nadie te haga creer lo contrario, ¡sobre todo aquellos de entre nosotros que solo quieren observar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9086') AND (`Locale`='esES');
