UPDATE `npc_text_locale` SET `ID`='9106', `Locale`='esES', `Text0_0`='Si nuestros enemigos se diesen cuenta de que lo único que queremos es vivir en armonía con ellos, puede que no necesitáramos atacarlos para sobrevivir.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9106') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9108', `Locale`='esES', `Text0_0`='Con nuevos enemigos a la vuelta de la esquina, ¡no debemos permitir a nuestros antiguos enemigos que se aprovechen de nuestra situación!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9108') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9111', `Locale`='esES', `Text0_0`='¡Asquerosos gigantes fúngicos! ¿Qué les hemos hecho nosotros?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9111') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9112', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Si no me hubiera torcido el tobillo viniendo a este lugar, estaría ahí dentro con mis amigos. Mmmm, ahora que lo pienso, quizás sea mejor que esté aquí fuera, ¿no?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9112') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9113', `Locale`='esES', `Text0_0`='Fhwoor gustar pequeños esporinos. Ellos salvar Fhwoor. No comerlos.$B$BFhwoor odiar nagas. ¡Fhwoor destrozar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9113') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9118', `Locale`='esES', `Text0_0`='Aquí yacen los restos del desherbador Pulgaverde, uno de los compañeros druidas del vigía Jhang. Todo indica que fue asesinado rápida y limpiamente mientras intentaba escapar del Recinto de los Esclavos.', `Text0_1`='Aquí yacen los restos del desherbador Pulgaverde, uno de los compañeros druidas del vigía Jhang. Todo indica que fue asesinado rápida y limpiamente mientras intentaba escapar del Recinto de los Esclavos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9118') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9119', `Locale`='esES', `Text0_0`='¡Casi lo consigo!$B$BVine aquí para demostrar la existencia de Quagmirran, el legendario gigante fúngico. Y lo conseguí; más o menos.$B$B¡Libérame! A cambio, te proporcionaré un beneficio para cuando tengas que tratar con Quagmirran.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9119') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9121', `Locale`='esES', `Text0_0`='¡Gracias al cielo que estás aquí! Zarpa y yo estábamos investigando este lugar cuando Musel\'ek, el señor del pantano, nos saltó encima. ¡Puede controlar a los animales, y hasta a los druidas!$B$BDesafortunadamente, Zarpa fue atrapado en forma de oso en aquel momento, y ahora es la mascota del señor del pantano. ¡No me atrevo a adoptar forma de gato, o me atrapará a mí también!$B$BMe temo que tendrás que luchar contra Zarpa para salvarlo. Sigue dirigiéndote al oeste, no tiene pérdida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9121') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9122', `Locale`='esES', `Text0_0`='Como único superviviente de los Tábidos, es un honor y una obligación continuar con las enseñanzas chamanísticas del clarividente Nobundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9122') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9124', `Locale`='esES', `Text0_0`='No me hagas perder el tiempo. ¿Qué es lo que quieres?', `Text0_1`='No me hagas perder el tiempo. ¿Qué es lo que quieres?', `Text1_0`='¿No ves que estoy ocupado?', `Text1_1`='¿No ves que estoy ocupado?', `Text2_0`='¿Sí? ¿Qué quieres?', `Text2_1`='¿Sí? ¿Qué quieres?', `Text3_0`='¿Y tú eres...?', `Text3_1`='¿Y tú eres...?', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9124') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9125', `Locale`='esES', `Text0_0`='¡Qué embarazoso, que un cazador te tome como mascota!$B$BTe debo la vida, pero, por favor, no te tomes más molestias por mi bienestar. Ahora que me has liberado de Musel\'ek, podré encontrar la forma de escapar. Quizás recoja a Rayge por el camino.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9125') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9128', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='En la naturaleza, todas las cosas están interconectadas en una red; una ecología. Si procuramos no alterarla, sino solo observarla, podremos tener la suerte de comprender las complejas relaciones que hay entre los organismos que nos rodean.$B$BUna vez que hayamos comprendido esto, podremos tomar las medidas necesarias para el bienestar de todas las criaturas. Intervenir sin ese conocimiento, provocaría su destrucción.$B$BYo estudio las relaciones entre los esporinos y los señores de la marisma.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9128') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9131', `Locale`='esES', `Text0_0`='Los druidas me protegen de los nagas y de los malvados Perdidos. A cambio, les enseño las plantas y los animales del pantano.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9131') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9144', `Locale`='esES', `Text0_0`='Ahora que la grosera interrupción ha terminado, como iba diciendo, ¡Quagmirran está cerca!$B$BSegún lo prometido, puedo fortalecerte con un favor mágico que debería ayudarte en tu lucha contra él.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9144') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9167', `Locale`='esES', `Text0_0`='Ysiel esperaba algo de resistencia enemiga a su incursión en Terrallende. Por eso se nos trajo a los celadores.$B$BNunca esperamos que nuestra resistencia viniera con la fuerza o los números que los nagas han mostrado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9167') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9174', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; al puesto de mando de los Caballeros de sangre, defensores de Quel\'Thalas, fieles siervos de Lunargenta y verdaderos maestros de la Luz.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9174') AND (`Locale`='esES');
