UPDATE `npc_text_locale` SET `ID`='9199', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Siempre me ha gustado ordenar y organizar cosas. Supongo que he salido a mi madre.', `Text1_0`=NULL, `Text1_1`='Aprender todo lo que se pueda sobre nuevas especies de plantas es muy importante para nuestra expedición. No solo de las comestibles o las medicinales.$B$BLas plantas son la base de otras muchas formas de vida. Comprenderlas mejor significa comprender mejor todo el sistema natural.', `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9199') AND (`Locale`='esES');
