UPDATE `npc_text_locale` SET `ID`='9204', `Locale`='esES', `Text0_0`='Gracias a ti, nuestros jóvenes estarán a salvo, $N. Ve a Esporaggar y visita a mi gente. Todos han oído hablar muy bien de ti.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9204') AND (`Locale`='esES');
