UPDATE `npc_text_locale` SET `ID`='9218', `Locale`='esES', `Text0_0`='Mi padre ha partido hacia lugares más exóticos en busca de mayores presas. Pero no te preocupes, ¡todavía hay mucho que cazar aquí!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9218') AND (`Locale`='esES');
