UPDATE `npc_text_locale` SET `ID`='9220', `Locale`='esES', `Text0_0`='Escucha, $gamigo:amiga;. He dejado la expedición en las competentes manos de mi hijo Junior. Cuando ya has cazado las mejores presas de Azeroth, empiezas a pensar en algo más grande.$B$B¿Y has visto las bestias que hay aquí?$B$B¡He venido a abatir la presa más peligrosa que hayas imaginado jamás! ¡Quiero cazar a Colmillo y llevarme su corazón como trofeo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9220') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9221', `Locale`='esES', `Text0_0`='¡$N! Recuerdo tus proezas de caza. Puede que no empezaras demasiado bien en Tuercespina, pero has seguido a tu ritmo y ahora eres uno de los mejores tras abatir al viejo Bangalash.$B$BPero escucha, $gamigo:amiga;, las presas de Terrallende ahora son lo máximo. ¡Mira mis cicatrices!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9221') AND (`Locale`='esES');
