UPDATE `npc_text_locale` SET `ID`='9227', `Locale`='esES', `Text0_0`='¿Tratar con los draenei, la gente que nos dio la espalda cuando sufríamos? No lo creo.$B$B¡Un espíritu recto no habla así! Muéstrate, impostor. ¿Quién te envía?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9227') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9229', `Locale`='esES', `Text0_0`='Así que debemos confiar en quienes nos insultan de esta manera. ¿Qué imagen tienen los draenei de nosotros como para usar trucos que llamen nuestra atención?$B$BSabemos que están deseando \"redimirnos\", pero mi pueblo no necesita redención alguna. Somos capaces de arreglárnoslas solos y perseguiremos nuestro destino como un pueblo independiente y orgulloso. Transmite este mensaje a tus aliados.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9229') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9231', `Locale`='esES', `Text0_0`='Bien. Asegúrate de que lo comprenden.$B$BAhora te agradecería que dejaras mi templo en paz.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9231') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9235', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; al Puerto Orebor, $gamigo:amiga;. Aquí no tienes nada que temer. Somos Tábidos, pero no somos enemigos.$B$BYo soy Ikuti de los Kurenai, un grupo de Tábidos que desean recuperar las relaciones con los hermanos draenei y sus nuevos aliados.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9235') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9236', `Locale`='esES', `Text0_0`='Maktu no te reconoce, $r, pero está encantado de verte. Si necesitas comprar o reparar armadura, ven a ver a Maktu.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9236') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9237', `Locale`='esES', `Text0_0`='Los recuerdos de este templo me envuelven la mente como una bruma espesa, pero no consigo formar una historia coherente con ellos. Este lugar significó algo para mí antaño. Hasta que pueda recuperar ese recuerdo, me sentiré como si faltara parte de mí.$B$BQuizás los draenei puedan ayudarme a recuperar lo que he perdido.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9237') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9238', `Locale`='esES', `Text0_0`='Algunos me llaman \"Tim\", pero para mis amigos soy \"el especialista\".', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9238') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9239', `Locale`='esES', `Text0_0`='Si lo preguntas, es que no hay confianza suficiente como para que uses ese apelativo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9239') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9240', `Locale`='esES', `Text0_0`='Saludos, $c. ¿Quieres cazar al límite? No busques más.$B$BHemet Nesingwary ha reunido a la mejor expedición jamás vista sobre los restos de este mundo. Ven con nosotros de safari si te atreves.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9240') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9241', `Locale`='esES', `Text0_0`='No dejes que las ruinas que nos rodean te engañen, $c. Dalaran es tan poderosa como siempre.', `Text0_1`='No dejes que las ruinas que nos rodean te engañen, $c. Dalaran es tan poderosa como siempre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9241') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9242', `Locale`='esES', `Text0_0`='Karazhan es más que un simple edificio, $N. Si trazaras las líneas-ley que atraviesan Azeroth, verías que todas convergen justo ahí.$B$B<Alturus señala un punto invisible bajo la torre.>$B$BFascinante, ¿verdad?', `Text0_1`=NULL, `Text1_0`='El archimago Alvareux escribió hace tiempo un tratado en el que desarrollaba una teoría fascinante. Proponía la existencia de una copia exacta de Karazhan en un mundo diferente al nuestro, pero que, de algún modo, compartía nuestro propio espacio.$B$BSe volvió loco poco después. Una lástima.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9242') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9243', `Locale`='esES', `Text0_0`='Los naaru poseen una sabiduría que no podemos comprender. He aprendido más cosas de A\'dal en los últimos cinco meses que en todos los años que pasé en las bibliotecas de Dalaran.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9243') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9248', `Locale`='esES', `Text0_0`='Bienvenido a Zabra\'jin, $c. Soy el cazador de las Sombras Denjai, supervisor de esta Avanzada y comandante de las fuerzas de Lanza Negra destacadas en la Marisma de Zangar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9248') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9249', `Locale`='esES', `Text0_0`='¡Ni se os ocurra pensar en intentar huir de mí, bebés múrloc! ¡Tengo ojos de halcón y orejas de... orejas de... de algo con orejas muy poderosas!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9249') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9250', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Tor\'gash cree que puede ver el futuro en las entrañas de los pájaros o lanzando los huesos de caña de sus ancestros. En realidad todo lo que hace falta es preguntar a los espíritus. Pero no intentes contarle esto, $N. Es muy terco.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9250') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9256', `Locale`='esES', `Text0_0`='Has demostrado ser $gun cazador:una cazadora; muy capaz, $N. Pero todavía hay una bestia que se aparece en tus sueños... ¡Colmillo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9256') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9258', `Locale`='esES', `Text0_0`='Y ahí estaba, cara a cara con un venado talbuk y armado solo con mi cuchillo... <tose>... y mi inteligencia de cazador curtido que me salvaría el pellejo.$B$BDe repente, ¡veo que un par de vientorrocs cazadores se lanzan sobre mí desde el cielo! Su ataque a traición... <tose> no me ayudó demasiado tampoco.$B$BMe quedé con sus picos de trofeo, pero también tengo una cicatriz infectada de recuerdo. Es muy fea y me duele mucho, pero estoy seguro de que curará en un día o dos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9258') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9259', `Locale`='esES', `Text0_0`='La primera vez que vi setas más altas que árboles, no tenía tanta seguridad en que pudiera llamar a este lugar hogar. Después de un tiempo, sencillamente dejas de darte cuenta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9259') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9260', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No olvides tomarte tu tiempo para pescar algo mientras estés en la Marisma de Zangar. Nunca sabes cuándo volverás a estar cerca del agua.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9260') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9261', `Locale`='esES', `Text0_0`='Telredor era un refugio para sacerdotes draenei y peregrinos. Los sacerdotes residentes casi no reciben visitas.$B$BCuando los orcos lanzaron sus primeros ataques a los asentamientos draenei, los refugiados empezaron a llegar en masa. El anacoreta Ahuurn y los demás transformaron Telredor en un refugio seguro. Muchos draenei, yo incluido, debemos nuestras vidas a sus esfuerzos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9261') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9262', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Las vistas desde la cima de Telredor son magníficas, ¿verdad?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9262') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9263', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; a Telredor, $c. Si necesitas algo, no dudes en preguntar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9263') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9264', `Locale`='esES', `Text0_0`='Nunca había visto tantos tipos diferentes de setas en un mismo sitio. Si me dieran el tiempo suficiente, estoy seguro de que podría encontrar un uso para cada una.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9264') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9265', `Locale`='esES', `Text0_0`='Telredor ha cambiado más en los últimos veinte años que en los siglos pasados. Yo no me quejo, $N; no es la primera vez que cambia el mundo.$B$BLa Luz me ha traído amigos y compañeros que no habría conocido como monje de clausura. Aprende lo que puedas del pasado mientras estás aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9265') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9267', `Locale`='esES', `Text0_0`='El cazador de las Sombras Denjai me ha enviado con un pequeño contingente de albañiles y guerreros para establecerse en esta Avanzada. ¡Apenas hemos terminado de construirla y ya nos pide informes!$B$BSi necesitas material, puedo ofrecerte algunas cosas, pero aún esperamos nuestro primer envío.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9267') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9268', `Locale`='esES', `Text0_0`='¡Si te encuentras con el médico brujo Tor\'gash dile que sus brebajes y sus talismanes no funcionan! Hice exactamente lo que me dijo y Magasha sigue coladita por Surai.$B$B¡No te creas nada de lo que dice ese falso médico brujo!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9268') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9269', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Reavij es tan sutil como un kodo! Pero lo peor es que no se da cuenta de que se nota. Veo la forma en que me mira cuando cree que nadie lo ve.$B$BMe asusta. Desearía que el cazador de las Sombras Denjai no lo hubiera mandado con nosotros. Nos podríamos haber deshecho de él cuando montamos la Avanzada Rata del Pantano.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9269') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9271', `Locale`='esES', `Text0_0`='Garadar es un caos, $gextranjero:extranjera;. Desde que la Abuela enfermó, Garrosh ha caído en una profunda depresión. Desde luego no es el niño de su padre.$B$B<Kurkush suspira.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9271') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9274', `Locale`='esES', `Text0_0`='Los anuncios de recompensas por los líderes nagas de las tribus de Cresta Oscura y Escamas de Sangre están enumerados aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9274') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9275', `Locale`='esES', `Text0_0`='Han muerto. Todos...$B$BLos Sangreoscura pasaron a través de nosotros y algo que no puedo comprender me poseyó. Dime, ¿alguna vez has experimentado una rabia tan grande que sientes que arde todo tu ser? ¿Un ardor que marchita a los vivos? ¿Un fuego que quema la sangre de tus venas? Eso es lo que yo sentí.$B$B<Los ojos de Colmillosauro brillan con una luz roja durante un segundo.>$B$BYo fui el único que sobrevivió. Me rodearon treinta... Sangreoscura... No recuerdo nada más. Perdí el conocimiento y me desperté al poco tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9275') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9276', `Locale`='esES', `Text0_0`='Se avecinan tiempos oscuros, $gextranjero:extranjera;...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9276') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9277', `Locale`='esES', `Text0_0`='Los espíritus están muy agitados. Debemos encontrar la causa de tal alteración.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9277') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9283', `Locale`='esES', `Text0_0`='Hemet es el mejor cazador que conozco. Nos topamos en las colinas de Cuna del Invierno, en Azeroth, hace muchas lunas. Él y yo acechábamos a la misma presa: el gran felino conocido como Rak\'shiri.$B$BEn lugar de pelearnos por nuestra presa, hicimos una apuesta; quien consiguiera la piel del Sable de Hielo, podría exigirle algo al otro. Para mi vergüenza eterna, fui el que perdió.$B$B¿Tengo cara de haber ganado?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9283') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9286', `Locale`='esES', `Text0_0`='Sí, sí, me siento mejor, pero la verdad es que me gusta estar aquí abajo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9286') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9287', `Locale`='esES', `Text0_0`='El Consorcio lleva meses planeando esta operación. Queríamos encontrar el Oshu\'gun, el diamante más grande del universo.$B$BUnos minutos antes de que empezáramos, nos llegó un mensaje del príncipe-nexo Haramad pidiéndonos que esperemos hasta que se resolviera una cuestión política en Tormenta Abisal. He transformado la operación en un mercado negro de gemas mientras esperamos, pero nuestros beneficios se reducen cada minuto.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9287') AND (`Locale`='esES');
