UPDATE `npc_text_locale` SET `ID`='9367', `Locale`='esES', `Text0_0`='<Lantresor ríe ruidosamente.>$B$B¿Sabes dónde te has metido, $gestúpido:estúpida;? Este es territorio de los ogros. Antes de los orcos y antes de los draenei estaban los ogros. Y sabiendo eso, quieres empezar una guerra que perderás con toda seguridad... Puño de Roca te aplastará. Y si Puño de Roca no te aplasta, entonces será Mazo de Guerra.$B$BPero creo que tus líderes ya lo saben...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9367') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9368', `Locale`='esES', `Text0_0`='<Lantresor te mira como si viese a través de ti.>$B$BPuede que haya otra solución... Aceptaré lo que pide tu líder, pero solo si haces exactamente lo que diga.$B$BEste trato podría beneficiarnos mucho a los dos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9368') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9369', `Locale`='esES', `Text0_0`='Participarás en una serie de acontecimientos que harán que los orcos Mata\'penas de la Fortaleza Mata\'penas piensen que los ogros de Mazo de Guerra han atacado su base. Después, tendrá lugar otra serie de acontecimientos que harán que los ogros de Mazo de Guerra piensen que los orcos de Mata\'penas están atacando su base.$B$BSi lo haces, resolverás nuestros problemas y los tuyos.$B$BPuño de Roca recuperará su territorio y Mazo de Guerra, el Consejo de la Sombra y Puño de Roca dejarán de atacar a tu gente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9369') AND (`Locale`='esES');
