UPDATE `npc_text_locale` SET `ID`='9444', `Locale`='esES', `Text0_0`='Gracias de nuevo, $N, por ayudarme a descubrir lo que pasó en el matorral.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9444') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9451', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mi trabajo consiste en ocuparme de estas pobres almas. Acepto la ayuda de cualquier corazón bondadoso, ya sea Aldor o Arúspice.$B$BNecesitamos a tanta gente que no le diría que no ni a un ogro si supiera cómo hacer un torniquete. La política de la ciudad me importa bien poco.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9451') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9452', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No puedo creerlo. ¡Este etéreo no tiene ni un solo ojo de basilisco!$B$B¡Y su líder tiene la desfachatez de llamarse \'el Príncipe Contrabandista\'!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9452') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9457', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$GBienvenido:Bienvenida;, $N. Somos los Aldor, sacerdotes de la luz y siervos de Sha\'tar.$B$BPuedes quedarte con nosotros todo el tiempo que quieras.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9457') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9458', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Sha\'tar significa Nacidos de la luz\'. Es el nombre que dimos a los naaru que recuperaron Shattrath de los ejércitos de Illidan.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9458') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9459', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Shattrath es algo más que un cuartel general para la guerra que libramos contra Illidan y la Legión Ardiente. Es un santuario para los que quieren refugiarse de la violencia que nos rodea.$B$BSi no aceptáramos a los refugiados, seríamos unos hipócritas.$B$BSi preguntas por los Arúspices, te diré que, aunque no me gusten ellos ni su líder, no tenemos la autoridad para cuestionar la sabiduría de los naaru.$B$BSon tiempos difíciles, $N. Necesitamos a todos los aliados que podamos encontrar.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9459') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9471', `Locale`='esES', `Text0_0`='$GBienvenido:Bienvenida; al Bastión Allerian, $c.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9471') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9472', `Locale`='esES', `Text0_0`='Cuidado, $c. El bosque está lleno de peligros ocultos y de enemigos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9472') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9477', `Locale`='esES', `Text0_0`='Esto es sencillamente horrible.$B$BNo me importan nada nuestras propias incomodidades aquí, ¡pero debemos hacer algo para aliviar el dolor de los muertos de El Vertedero de Huesos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9477') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9481', `Locale`='esES', `Text0_0`='Si esperas algo parecido a una bienvenida oficial, siento decepcionarte, $c; aquí no seguimos demasiado las tradiciones. No nos gustan los artificios.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9481') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9482', `Locale`='esES', `Text0_0`='Una nota destaca de los demás pedazos de pergamino que están clavados en este cartel de \"Se busca\". Un llamamiento para matar a los Atizahuesos de El Vertedero de Huesos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9482') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9485', `Locale`='esES', `Text0_0`='Atizahuesos... ¡Los detesto a todos!$B$BSi no hubiera perdido mi ojo ni me salieran a veces esos hechizos tan débiles, estaría ahí fuera cargándomelos a todos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9485') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9486', `Locale`='esES', `Text0_0`='He estado observando cómo trabajan los Aldor en las murallas. Van a reconstruir toda la ciudad, ¿sabes?$B$BTrabajan mucho más de prisa que el viejo supervisor Olento en mi Villa del Lago natal. Constantemente perdía las herramientas en el lago, o los gnolls le robaban los suministros.$B$BDebería pedir a los Aldor que le envíen algo de ayuda. Ellos repararían el puente en menos que canta un gallo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9486') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9487', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hay un gran bosque ahí fuera, $c. Deja que el chamán juegue con los espíritus y el guerrero con las espadas. En Terokkar haré fortuna.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9487') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9488', `Locale`='esES', `Text0_0`='Malukaz no para de hablar de la importancia histórica de este bosque, pero yo no puedo permitirme el lujo de preocuparme por la historia hasta que no se arregle el presente.$B$BNo digo que su trabajo aquí no sea importante, pero alguien tiene que preocuparse de que funcionen las defensas y la logística del fuerte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9488') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9489', `Locale`='esES', `Text0_0`='Hasta que viajé por Nagrand y llegué al bosque, pensaba que una gran parte de nuestra cultura original se había perdido. Me alegra comprobar que estaba equivocado.$B$B¡Todavía queda mucho por hacer aquí!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9489') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9490', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La apertura del Portal Oscuro, trajo noticias de la suerte que corrió mi pueblo. En cierto modo, mi exilio evitó que compartiese su caída, pero ver a los Errantes darlo todo por Kael\'thas...$B$BNunca imaginé que los que fueron mis hermanos podrían hacer algo así.$B$BLa vuelta a casa con la que tanto soñé nunca tendrá lugar. Este bosque es el único hogar que me queda.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9490') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9492', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Cuado llegué aquí, esperaba estar a la altura de la reputación de la ciudad.$B$B<Ros\'eleth mira a su alrededor.>$B$BLa mayoría de los jóvenes superan la fase de \"quiero ser un Errante\" en cuanto alguien los acepta como aprendices. Entonces pasan a la fase \"yo quiero ser magister\".$B$BPara serte sincera, yo todavía estoy superando lo de \"quiero ser Errante\". Nadie quiere ser costurera de mayor, pero alguien tiene que hacer lo que se ponen los Errantes para no andar desnudos. Eso es lo que solía decirme mi madre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9492') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9493', `Locale`='esES', `Text0_0`='¿Has visto cómo me guiña el ojo Ros\'eleth cada vez que voy a recoger ropa o algún sombrero? Cuando las cosas se calmen por aquí, me la voy a llevar de paseo al bosque.$B$BAhora sí que echo de menos todas esas tabernas de Ventormenta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9493') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9494', `Locale`='esES', `Text0_0`='<Kaide te hace callar.>$B$B¡Silencio, o atraerás a los orcos y a la Alianza hacia nosotros, torpe patán!$B$BExacto, asiente con la cabeza, y cuando acabemos de hablar te irás y olvidarás que me viste aquí.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9494') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9495', `Locale`='esES', `Text0_0`='Puede que estemos juntos en esto, pero eso solo hace que sea más necesaria la discreción. Te lo repito: ten cuidado con lo que dices y con lo que haces.$B$BTe garantizo que no somos los únicos aquí fuera.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9495') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9497', `Locale`='esES', `Text0_0`='Sí, ¿qué quieres? ¿No ves que estoy ocupado? ¡El Consejo de la Sombra no me paga por hora de trabajo!', `Text0_1`='Sí, ¿qué quieres? ¿No ves que estoy ocupada? ¡El Consejo de la Sombra no me paga por hora de trabajo!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9497') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9498', `Locale`='esES', `Text0_0`='¿Qué parece que estoy haciendo? Estoy arreglando las cosas para que podamos instalarnos. Y yo solo, además.$B$BCon una base de operaciones en el bosque será más fácil seguir la pista a lo que traman los druidas de Shattrath, Allerian, Rompepedras y Cenarion. Nuestra base en Auchindoun es demasiado poco práctica para eso.$B$BEspera un momento, ¿te conozco?', `Text0_1`='¿Qué parece que estoy haciendo? Estoy arreglando las cosas para que podamos instalarnos. Y yo sola, además.$B$BCon una base de operaciones en el bosque será más fácil seguir la pista a lo que traman los druidas de Shattrath, Allerian, Rompepedras y Cenarion. Nuestra base en Auchindoun es demasiado poco práctica para eso.$B$BEspera un momento, ¿te conozco?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9498') AND (`Locale`='esES');
