UPDATE `npc_text_locale` SET `ID`='9503', `Locale`='esES', `Text0_0`='¡Entrenando! Pronto seré lo suficientemente bueno como para unirme a los otros invocadores y entrar en el Laberignore into de las Sombras de Auchindoun.$B$B¡Espero poder invocar algo grande! ¡Quizás algo tan grande como Murmur, aunque no perdería el control sobre él!', `Text0_1`='¡Entrenando! Pronto seré lo suficientemente buena como para unirme a los otros invocadores y entrar en el Laberignore into de las Sombras de Auchindoun.$B$B¡Espero poder invocar algo grande! ¡Quizás algo tan grande como Murmur, aunque no perdería el control sobre él!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9503') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9505', `Locale`='esES', `Text0_0`='¿Y qué es exactamente lo que puedo hacer por ti? ¿No te das cuenta de que estoy muy ocupado discutiendo cuestiones importantes con nuestro ilustre líder?$B$BDate prisa en hablar o haré que te den una paliza, gusano.', `Text0_1`='¿Y qué es exactamente lo que puedo hacer por ti? ¿No te das cuenta de que estoy muy ocupada discutiendo cuestiones importantes con nuestro ilustre líder?$B$BDate prisa en hablar o haré que te den una paliza, gusano.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9505') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9506', `Locale`='esES', `Text0_0`='Bueno, yo solo estaba informando a nuestro líder sobre los avances y sobre cómo todo sale según lo previsto. Solo tardaremos unas semanas más en tenerlo todo arreglado.$B$BPuede que nos convenga que el Consejo de la Sombra contrate a orcos para el pueblo. Así alegaremos derecho ancestral y podremos trabajar ocultos.$B$BUn momento. ¿Te conozco? ¿Cómo te llamas?', `Text0_1`='Bueno, yo solo estaba informando a nuestro líder sobre los avances y sobre como todo sale según lo previsto. Solo tardaremos unas semanas más en tenerlo todo arreglado.$B$BPuede que nos convenga que el Consejo de la Sombra contrate a orcos para el pueblo. Así alegaremos derecho ancestral y podremos trabajar en la sombra.$B$BUn momento. ¿Te conozco? ¿Cómo te llamas?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9506') AND (`Locale`='esES');
