UPDATE `npc_text_locale` SET `ID`='9610', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Gracias por ayudar a Thrall a escapar, amigos. Ahora, espero que también podáis ayudarme a mí. Me desperté esta mañana atrapada en esta prisión mágica. Un extraño zahorí me dijo que me quedaría un tiempo aquí.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9610') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9613', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Sí, amigos. Ese hombre no era un zahorí de Dalaran. Sus ropas y acento no me resultaban familiares. Todo lo que decía es el tiempo esto, el tiempo lo otro... Tendréis que encontrar al zahorí y convencerle para que me suelte.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9613') AND (`Locale`='esES');
