UPDATE `npc_text_locale` SET `ID`='9615', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Como representante del Bajo Arrabal, estoy aquí para exponer nuestro caso, por el que Auchindoun debe ser evacuada de su infestación del Consejo de la Sombra. Esto es, ¡si queremos albergar esperanzas de tener un futuro y evitar la posibilidad de un cataclismo mayor que la creación de El Vertedero de Huesos!$B$BTodas nuestras gentes han sufrido ya demasiado. ¡Estoy aquí para encargarme de que ese sufrimiento se detenga!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9615') AND (`Locale`='esES');
