UPDATE `npc_text_locale` SET `ID`='9630', `Locale`='esES', `Text0_0`='Mi hermana Silanna es muy ingenua, no entiende el peligro creciente que habita en estos bosques. Pero de todas formas, en cuanto cargue el carro, nos vamos a la ciudad. $B$BParece ser que tengo más bolsas de las que necesito. Quizás a ti te vengan bien.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9630') AND (`Locale`='esES');
