UPDATE `npc_text_locale` SET `ID`='9632', `Locale`='esES', `Text0_0`='Te agradezco muchísimo tu ayuda con las minas. Ahora, solo hay que convencer a los mineros para que vuelvan al trabajo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9632') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='9634', `Locale`='esES', `Text0_0`='<Isfar suspira.>$B$BUn par de casas en los árboles no hacen un hogar, pero ha sido una gran mejora en las Salas Sethekk.$B$BAunque en Shattrath estaba perfectamente bien, no soy capaz de regresar a mi verdadero hogar en Skettis sin mi hermana. Está atrapada en las Salas Sethekk. La tiene prisionera nuestro hermano mayor.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='9634') AND (`Locale`='esES');
